import Play from "../Play";
import React, { useState, useEffect, useRef } from "react";
import { renderGrid } from "./utils";
import { RoundBase } from "../../type";
import { useHost } from "../../context/hostContext";
import { usePlayer } from "../../context/playerContext";
import { setSelectedRow } from "../../components/services";
import { useSearchParams } from "react-router-dom";

interface HintWord {
  word: string;
  x: number;
  y: number;
  direction: "horizontal" | "vertical";
}

interface ObstacleQuestionBoxProps {
  obstacleWord?: string;
  hintWordArray?: string[];
  isHost?: boolean;
  initialGrid?: string[][];
}

const mainKeyword = "BÒCÔNGANH";

const QuestionBoxRound2: React.FC<ObstacleQuestionBoxProps> = ({
  obstacleWord = mainKeyword,
  hintWordArray,
  initialGrid,
  isHost = false,
}) => {
  console.log("hintWordArray", typeof hintWordArray);
  console.log("obstacle", obstacleWord);

  const [searchParams] = useSearchParams();
  const { setInitialGrid } = usePlayer();
  const roomId = searchParams.get("roomId") || "";
  const GRID_SIZE = 20;

  const generateEmptyGrid = () => {
    return Array(GRID_SIZE)
      .fill(null)
      .map(() => Array(GRID_SIZE).fill(""));
  };

  const [grid, setGrid] = useState<string[][]>(generateEmptyGrid());
  const [revealedRows, setRevealedRows] = useState<boolean[]>(Array(GRID_SIZE).fill(false));
  const [revealedCols, setRevealedCols] = useState<boolean[]>(Array(GRID_SIZE).fill(false));
  const [hintWords, setHintWords] = useState<HintWord[]>([]);
  const [cellStyles, setCellStyles] = useState<Record<string, string>>({}); // Tracks background styles
  const [menu, setMenu] = useState<{
    visible: boolean;
    rowIndex?: number;
    colIndex?: number;
  }>({ visible: false });

  const menuRef = useRef<HTMLDivElement>(null);

  // Initialize grid
  useEffect(() => {
    if (isHost) {
      const wordList = ["GIÓ", "HẠTGIỐNG", "DREAM", "THUỐC", "CÚC", "CHÂUKIỆTLUÂN"];
      console.log("wordList", wordList);
      console.log("hintWordArray", hintWordArray);
      if (hintWordArray && obstacleWord) {
        const { randomHintWords, newGrid } = renderGrid(hintWordArray, obstacleWord, GRID_SIZE);
        console.log("randomHintWords", randomHintWords);
        console.log("newGrid", newGrid);
        setHintWords(randomHintWords);
        setInitialGrid(newGrid);
        setGrid(newGrid);
      }
    } else {
      if (initialGrid) {
        setGrid(initialGrid);
      }
    }
  }, [isHost, hintWordArray, obstacleWord, initialGrid, setInitialGrid]);

  // Host: Toggle row
  const toggleRow = (rowIndex: number) => {
    if (!isHost) return;
    setRevealedRows((prev) => {
      const newRevealed = [...prev];
      newRevealed[rowIndex] = !newRevealed[rowIndex];
      return newRevealed;
    });
    setSelectedRow(roomId, rowIndex.toString());
  };

  // Host: Toggle column
  const toggleCol = (colIndex: number) => {
    if (!isHost) return;
    setRevealedCols((prev) => {
      const newRevealed = [...prev];
      newRevealed[colIndex] = !newRevealed[colIndex];
      return newRevealed;
    });
    setSelectedRow(roomId, colIndex.toString());
  };

  // Host: Handle number click to show menu
  const handleNumberClick = (rowIndex: number, colIndex: number) => {
    if (!isHost) return;
    setMenu({
      visible: true,
      rowIndex,
      colIndex,
    });
  };

  // Host: Handle menu actions
  const handleMenuAction = (action: "open" | "correct" | "incorrect", rowIndex: number, colIndex: number, hintWordNumber: string) => {
    const isRow = hintWords.some(
      (word) => word.y === rowIndex && word.direction === "horizontal"
    );
    if (action === "open") {
      if (isRow) {
        toggleRow(rowIndex);
        // Set row cells to yellow
        setCellStyles((prev) => {
          const newStyles = { ...prev };
          if(hintWordArray) {
            for (let col = colIndex; col < hintWordArray[parseInt(hintWordNumber)-1].length; col++) {
              newStyles[`${rowIndex}-${col}`] = "bg-yellow-200";
            }
          }
          return newStyles;
        });
      } else {
        toggleCol(colIndex);
        // Set column cells to yellow
        setCellStyles((prev) => {
          const newStyles = { ...prev };
          if(hintWordArray) {
            for (let row = rowIndex; row < hintWordArray[parseInt(hintWordNumber)-1].length + rowIndex; row++) {
              newStyles[`${row}-${colIndex}`] = "bg-yellow-200";
            }
          }
          return newStyles;
        });
      }
    } else if (action === "correct") {
      if (isRow) {
        toggleRow(rowIndex); // Reveal row
      } else {
        toggleCol(colIndex); // Reveal column
      }
    } else if (action === "incorrect") {
      if (isRow) {
        // Set row cells to grey
        setCellStyles((prev) => {
          const newStyles = { ...prev };
          for (let col = 0; col < GRID_SIZE; col++) {
            newStyles[`${rowIndex}-${col}`] = "bg-gray-400";
          }
          return newStyles;
        });
      } else {
        // Set column cells to grey
        setCellStyles((prev) => {
          const newStyles = { ...prev };
          for (let row = 0; row < GRID_SIZE; row++) {
            newStyles[`${row}-${colIndex}`] = "bg-gray-400";
          }
          return newStyles;
        });
      }
    }
    setMenu({ visible: false });
  };

  // Close menu on outside click
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (menuRef.current && !menuRef.current.contains(event.target as Node)) {
        setMenu({ visible: false });
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div className="flex flex-col items-center bg-white rounded-lg shadow-md p-6 relative">
      <div className="text-gray-700 text-xl font-semibold text-center mb-4 max-w-[90%]">
        Trong các giải đấu thể thao, những đội hay vận động viên mạnh sẽ được xếp vào cùng một hoặc nhiều nhóm trong khi bốc thăm để tránh việc gặp nhau sớm. Tên gọi chung của các nhóm vận động viên này là gì?
      </div>
      <div className="grid grid-cols-[repeat(20,40px)] grid-rows-[repeat(20,40px)] gap-1 max-h-[400px] overflow-y-scroll">
        {grid.map((row, rowIndex) => (
          <React.Fragment key={rowIndex}>
            {row.map((cell, colIndex) => {
              const isRevealed =
                (revealedRows[rowIndex] &&
                  hintWords.some(
                    (word) => word.y === rowIndex && word.direction === "horizontal"
                  )) ||
                (revealedCols[colIndex] &&
                  hintWords.some(
                    (word) => word.x === colIndex && word.direction === "vertical"
                  ));

              const cellKey = `${rowIndex}-${colIndex}`;
              const cellBg = cellStyles[cellKey] || (cell === "" ? "bg-white" : "bg-gray-50");

              const showMenu =
                menu.visible &&
                menu.rowIndex === rowIndex &&
                menu.colIndex === colIndex &&
                cell.includes("number");

              return (
                <div className="relative flex items-center">
                  <div
                    key={colIndex}
                    className={`w-10 h-10 flex items-center justify-center text-lg font-semibold select-none 
                      ${cell.includes("number") ? "text-blue-500 border-none" : ""}
                      ${cellBg}
                      ${
                        !cell.includes("number") &&
                        isRevealed &&
                        obstacleWord &&
                        !obstacleWord.includes(cell)
                          ? "text-black"
                          : !cell.includes("number")
                          ? "text-transparent"
                          : ""
                      }
                      ${
                        obstacleWord?.includes(cell) && isRevealed && isNaN(Number(cell))
                          ? "font-bold text-red-500"
                          : ""
                      }`}
                    onClick={() => {
                      if (isHost) {
                        if (cell.includes("number")) {
                          handleNumberClick(rowIndex, colIndex);
                        } else if (
                          hintWords.some(
                            (word) => word.y === rowIndex && word.direction === "horizontal"
                          )
                        ) {
                          toggleRow(rowIndex);
                        } else if (
                          hintWords.some(
                            (word) => word.x === colIndex && word.direction === "vertical"
                          )
                        ) {
                          toggleCol(colIndex);
                        }
                      }
                    }}
                    style={{
                      cursor:
                        isHost &&
                        (cell.includes("number") ||
                          hintWords.some(
                            (word) => word.y === rowIndex || word.x === colIndex
                          ))
                          ? "pointer"
                          : "default",
                    }}
                  >
                    {cell.includes("number") ? cell.replace("number", "").trim() : cell}
                  </div>
                  {showMenu && (
                    <div
                      ref={menuRef}
                      className="absolute left-12 top-1/2 transform -translate-y-1/2 flex space-x-2 bg-white border border-gray-300 rounded shadow-lg p-1 z-10"
                    >
                      <button
                        className="px-2 py-1 text-sm bg-blue-500 text-white rounded hover:bg-blue-600"
                        onClick={() => handleMenuAction("open", rowIndex, colIndex, cell)}
                      >
                        SELECT
                      </button>
                      <button
                        className="px-2 py-1 text-sm bg-green-500 text-white rounded hover:bg-green-600"
                        onClick={() => handleMenuAction("correct", rowIndex, colIndex, cell)}
                      >
                        Correct
                      </button>
                      <button
                        className="px-2 py-1 text-sm bg-red-500 text-white rounded hover:bg-red-600"
                        onClick={() => handleMenuAction("incorrect", rowIndex, colIndex, cell)}
                      >
                        Incorrect
                      </button>
                    </div>
                  )}
                </div>
              );
            })}
          </React.Fragment>
        ))}
      </div>
    </div>
  );
};

export default QuestionBoxRound2;
