    for (let i = 0; i < word.char.length; i++) {
      const xIndex = matchData.dir === 0 ? matchData.x + i : matchData.x;
      const yIndex = matchData.dir === 0 ? matchData.y : matchData.y + i;
      expandBoard(board, xIndex, yIndex);
