    wordsActive.push(word);
    return true;
  }

  // H√†m ch√≠nh ƒë·ªÉ ƒë·∫∑t t·∫•t c·∫£ t·ª´
  while (wordBank.length > 0) {
    let minMatchDiff = Infinity;
