    }

    // ƒê·∫∑t t·ª´ v√† lo·∫°i kh·ªèi wordBank
    const wordToPlace = wordBank.splice(curIndex, 1)[0];
    if (!placeWord(wordToPlace, board, wordsActive)) {
      return {
