                    }
                }

                if (!placed) {
                    console.warn(`Kh√¥ng th·ªÉ ƒë·∫∑t c·∫∑p t·ª´: ${word1} - ${word2}`);
                    allPlaced = false;
                    break;
                }
            }



            for (const word of words) {
                if (placedSet.has(word)) continue;

