import Round1 from '../../layouts/RoundBase/Round1';
import User from '../../layouts/User/User';
import { listenToRoundStart } from '../../services/firebaseServices';
import { useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { usePlayer } from '../../context/playerContext';

function UserRound1() {
    const [searchParams] = useSearchParams()
    const navigate = useNavigate()
    const roomId = searchParams.get("roomid") || "1"
    useEffect(() => {
        const unsubscribePlayers = listenToRoundStart(roomId, (round) => {
            navigate(`/play?round=${round}&roomId=`)
        });

        return () => {
            unsubscribePlayers();
        };
    }, []);
    return (
        <User
            QuestionComponent={<Round1 />}
        />
    );
}

export default UserRound1
