import React, { useEffect, useState } from 'react';
import { Score } from '../type';
import { usePlayer } from '../context/playerContext';
import { listenToScores } from '../services/firebaseServices';

function PlayerScore() {
    const { roomId } = usePlayer();
    const [scoreList, setScoreList] = useState<Score[]>(Array(4).fill(null).map(() => ({
        score: "0",
        isCorrect: false,
        playerName: "",
        avatar: "",
    })));

    useEffect(() => {
        const unsubscribePlayers = listenToScores(roomId, (scoreList) => {
            console.log("scoreList", scoreList);
            if (Array.isArray(scoreList)) {
                const sortedList = [...scoreList].sort((a, b) => parseInt(b.score) - parseInt(a.score));
                setScoreList(sortedList);
            }
        });

        return () => {
            unsubscribePlayers();
        };
    }, [roomId]); // Add roomId as dependency

    return (
        <div className="bg-white mt-4 p-4 rounded-lg shadow-md flex-1 relative" style={{ height: `${scoreList.length * 50}px` }}>
            {scoreList.map((player, index) => (
                <div
                    key={player.uid || player.playerName} // Use uid if available, otherwise playerName
                    className="player-item absolute w-full flex items-center"
                    style={{
                        top: `${index * 50}px`,
                        transition: 'top 0.5s ease', // Smoothly animate vertical movement
                    }}
                >
                    <img
                        src={player.avatar}
                        alt={player.playerName}
                        className="w-8 h-8 rounded-full mr-2"
                    />
                    <div className="flex-1">
                        <p className="text-sm">{player.playerName}</p>
                        <div className="w-full bg-gray-200 text-center py-1 rounded-lg">
                            {`Score: ${player.score}`}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default PlayerScore;
