import { Question } from "../../../type";
import useAuth from "../../../hooks/useAuth";

export const getTest = async (testName: string, userId: string, getToken: () => Promise<string | null>): Promise<any> => {
  try {

    const { getToken } = useAuth()
    const token = await getToken();
    if (!token) {
      throw new Error("No token found. Please log in.");
    }
    const response = await fetch(`http://127.0.0.1:8000/api/test/${testName}?test_owner=${userId}`, {
      method: 'GET', // Phương thức HTTP
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`, // Nếu API yêu cầu token
      },
    });

    if (!response.ok) {
      throw new Error(`Failed to fetch test with ID: ${testName}, Status: ${response.status}`);
    }

    // Phân tích dữ liệu từ response JSON
    const data = await response.json();
    return data;

  } catch (error) {
    console.error('Error fetching test data:', error);
    throw error; // Quăng lỗi để xử lý ở nơi gọi hàm
  }
};

export const uploadTestToServer = async (file: File, testName: string): Promise<{ message: string; data?: any }> => {
  // Kiểm tra nếu không có file
  if (!file) {
    throw new Error("Không có file nào được chọn!");
  }

  const { getToken } = useAuth()
  const token = await getToken();
  if (!token) {
    throw new Error("No token found. Please log in.");
  }
  // Tạo đối tượng FormData để đóng gói file
  const formData = new FormData();
  formData.append("file", file);

  try {
    // Gửi yêu cầu POST tới server
    const response = await fetch(`http://127.0.0.1:8000/api/test/upload?test_name=${testName}&test_owner=mnbvcxz`, {
      method: "POST",
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`, // Nếu API yêu cầu token
      },
      body: formData,
    });

    // Kiểm tra phản hồi HTTP
    if (response.status == 400) {
      throw new Error(`Upload thất bại: Bộ đề đã tồn tại! chọn bộ đề khác`);
    }

    if (!response.ok) {
      throw new Error(`Upload thất bại: ${response.statusText}`);
    }

    // Giải mã JSON từ server
    const result = await response.json();
    console.log("Upload thành công:", result);
    return { message: "Upload thành công!", data: result };
  } catch (error: any) {
    console.error("Lỗi khi upload file:", error.message);
    throw new Error(`Lỗi upload: ${error.message}`);
  }
};

export const getTestByUserId = async (userId: string): Promise<any> => {
  try {

    const { getToken } = useAuth()
    const token = await getToken();
    if (!token) {
      throw new Error("No token found. Please log in.");
    }
    const response = await fetch(`http://127.0.0.1:8000/api/test/user/${userId}`, {
      method: 'GET', // Phương thức HTTP
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`, // Nếu API yêu cầu token
      },
    });

    if (!response.ok) {
      throw new Error(`Failed to fetch test with ID: ${userId}, Status: ${response.status}`);
    }

    // Phân tích dữ liệu từ response JSON
    const data = await response.json();
    return data;

  } catch (error) {
    console.error('Error fetching test data:', error);
    throw error; // Quăng lỗi để xử lý ở nơi gọi hàm
  }
};

export const updateQuestion = async (updateQuestion: Partial<Question>, question_id: string): Promise<any> => {
  try {
    const { getToken } = useAuth()
    const token = await getToken();
    if (!token) {
      throw new Error("No token found. Please log in.");
    }

    const response = await fetch(`http://127.0.0.1:8000/api/test/update/${question_id}`, {
      method: 'PUT', // Phương thức HTTP
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`, // Nếu API yêu cầu token
      },
      body: JSON.stringify(updateQuestion)
    });

    if (!response.ok) {
      throw new Error(`Failed to fetch test with ID: ${question_id}, Status: ${response.status}`);
    }

    // Phân tích dữ liệu từ response JSON
    const data = await response.json();
    return data;

  } catch (error) {
    console.error('Error fetching test data:', error);
    throw error; // Quăng lỗi để xử lý ở nơi gọi hàm
  }
};
