import React, { useState, useEffect } from "react";
import Play from "../../layouts/Play";

interface CrosswordQuestionBoxProps {
    obstacleWord: string;
    hintWords: string[];
}

const CrosswordQuestionBox: React.FC<CrosswordQuestionBoxProps> = ({
    obstacleWord,
    hintWords,
}) => {
    const [highlightedLetters, setHighlightedLetters] = useState<Set<string>>(new Set());

    useEffect(() => {
        // Lấy danh sách các chữ cái chung giữa từ khóa chính và từ gợi ý
        const commonLetters = new Set<string>();
        hintWords.forEach(hint => {
            for (const char of hint) {
                if (obstacleWord.includes(char)) {
                    commonLetters.add(char);
                }
            }
        });

        // Random chọn một số chữ cái chung để tô đậm
        const commonArray = Array.from(commonLetters);
        const selectedHighlights = new Set<string>(
            commonArray.sort(() => 0.5 - Math.random()).slice(0, Math.ceil(commonArray.length / 2))
        );

        setHighlightedLetters(selectedHighlights);
    }, [obstacleWord, hintWords]);

    return (
        <div className="flex flex-col items-center bg-white p-6 rounded-lg shadow-lg w-full">
            <h2 className="text-xl font-bold text-center mb-4">Chướng Ngại Vật</h2>

            {/* Hiển thị từ khóa chính */}
            <div className="text-2xl font-bold text-red-500">
                {obstacleWord.split("").map((char, index) => (
                    <span key={index} className={highlightedLetters.has(char) ? "text-blue-600 font-extrabold" : ""}>
                        {char}
                    </span>
                ))}
            </div>

            <h3 className="text-lg font-semibold mt-4">Gợi ý</h3>

            {/* Hiển thị các từ gợi ý theo kiểu crossword đơn giản */}
            <div className="flex flex-col items-center">
                {hintWords.map((hint, i) => (
                    <div key={i} className="text-xl font-medium">
                        {hint.split("").map((char, index) => (
                            <span key={index} className={highlightedLetters.has(char) ? "text-blue-600 font-extrabold" : ""}>
                                {char}
                            </span>
                        ))}
                    </div>
                ))}
            </div>
        </div>
    );
};

function Round2() {
    return (
        <Play
            questionComponent={<Play questionComponent={<CrosswordQuestionBox obstacleWord="BỒ CÔNG ANH" hintWords={["GIÓ", "HẠTGIỐNG", "DREAM", "CHÂUKIỆTLUÂN", "THUỐC", "CÚC"]} />} />
            }
        />
    )
}

export default Round2
