import { useEffect, useState } from "react";
import User from "../../../layouts/User/User";
import Round2 from "../../../layouts/RoundBase/Round2";
import ReactPlaceholder from "react-placeholder";
import "react-placeholder/lib/reactPlaceholder.css";

function UserRound2() {


    const [loading, setLoading] = useState(true);
    const [data, setData] = useState<string>("");

    useEffect(() => {
        setTimeout(() => {
            setData("loaded");
            setLoading(false);
        }, 3000); // Simulating an API call
    }, []);

    useEffect(() => {
            const unsubscribePlayers = listenToQuestions(roomId, (question) => {
                setCurrentQuestion(question)
                console.log("current question", question)
                if(question.imgUrl){
                    const match = question.imgUrl.match(/\/d\/(.+?)\//);
                    const fileId = match ? match[1] : null; 
                    console.log("image url", question.imgUrl.match(/\/d\/(.+?)\//))
                    setFinalUrl(`https://drive.usercontent.google.com/download?id=${fileId}&export=view`)
                }
                
            });
    
            // No need to set state here; it's handled by useState initializer
            return () => {
                unsubscribePlayers();
            };
        }, []);

    return (
        <ReactPlaceholder
            type="text"
            rows={3}
            ready={!loading}
        >
            <>
            </>
            {/* <User
                QuestionComponent={<Round2/>}
            /> */}
        </ReactPlaceholder>
    )

}

export default UserRound2;
