import React, { useState, useEffect } from "react";

// Initialize word bank based on user input
const wordBank = ["GIÓ", "HẠT GIỐNG", "DREAM", "THUỐC", "CÚC", "CHÂU KIỆT LUÂN"];
const boardSize = 20;

// Represents a word object with its position and direction
interface WordObj {
  word: string;
  x: number;
  y: number;
  dir: number; // 0: horizontal, 1: vertical
  char: string[];
  totalMatches: number;
  successfulMatches: { x: number; y: number; dir: number }[];
}

// Function to create an empty board
const generateBoard = (size: number): string[][] => {
  return Array(size).fill(null).map(() => Array(size).fill(""));
};

// Prepare word bank from word array
const prepareWordBank = (): WordObj[] => {
  return wordBank.map((word, i) => {
    return {
      word: ` ${i + 1}${word} `,
      x: -1,
      y: -1,
      dir: 0,
      char: word.split(""),
      totalMatches: 0,
      successfulMatches: [],
    };
  });
};

// Apply placement logic to populate board
const populateBoard = (wordBank: WordObj[], board: string[][]): WordObj[] => {
  const wordsActive: WordObj[] = [];

  for (let i = 0; i < wordBank.length; i++) {
    const isPlaced = addWordToBoard(wordBank[i], board, wordsActive);
    if (!isPlaced) break; // Stop placing if a word fails
  }
  return wordsActive;
};

// Logic to add words to the board
const addWordToBoard = (word: WordObj, board: string[][], wordsActive: WordObj[]): boolean => {
  let curWord = word;
  let curIndex = -1;
  let minMatchDiff = 9999;

  if (wordsActive.length < 1) {
    curWord.x = 10;
    curWord.y = 10;
    curWord.dir = 0;
    wordsActive.push(curWord);
  } else {
    for (let i = 0; i < wordBank.length; i++) {
      let curMatchDiff = wordBank[i].totalMatches;
      if (curMatchDiff < minMatchDiff) {
        minMatchDiff = curMatchDiff;
        curIndex = i;
      }
    }

    if (curIndex === -1) return false;
    curWord = wordBank[curIndex];
    wordBank.splice(curIndex, 1);
    wordsActive.push(curWord);

    for (let i = 0; i < curWord.char.length; i++) {
      let xIndex = curWord.x, yIndex = curWord.y;
      if (curWord.dir === 0) xIndex += i;
      else yIndex += i;
      board[xIndex][yIndex] = curWord.char[i];
    }
  }
  return true;
};

// Convert board state into HTML for rendering
const BoardToHtml = (board: string[][]) => {
  return (
    <div style={{ display: "grid", gridTemplateColumns: `repeat(${boardSize}, 30px)` }}>
      {board.map((row, rowIndex) =>
        row.map((cell, colIndex) => (
          <div
            key={`${rowIndex}-${colIndex}`}
            style={{
              width: 30,
              height: 30,
              border: "1px solid black",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              fontWeight: "bold",
            }}
          >
            {cell}
          </div>
        ))
      )}
    </div>
  );
};

// Main React Component
const WordBoard: React.FC = () => {
  const [board, setBoard] = useState<string[][]>(generateBoard(boardSize));
  const [wordsPlaced, setWordsPlaced] = useState<WordObj[]>([]);

  useEffect(() => {
    const wordBank = prepareWordBank();
    const wordsActive = populateBoard(wordBank, board);
    setWordsPlaced(wordsActive);
    setBoard([...board]); // Refresh board state
  }, []);

  return (
    <div>
      <h2>Word Puzzle Board</h2>
      {BoardToHtml(board)}
    </div>
  );
};

export default WordBoard;
