import React, { useState } from 'react';
import QuestionBox from './QuestionBox';
import Header from './Header';

const Play: React.FC = () => {
    const [isChatOpen, setIsChatOpen] = useState(false);
    const [timeLeft, setTimeLeft] = useState(30);

    const questionData = {
        question: "What is the capital of France? This is a long question example to demonstrate the UI behavior when handling lengthy text.",
        imageUrl: "https://a.travel-assets.com/findyours-php/viewfinder/images/res70/474000/474240-Left-Bank-Paris.jpg",
    };

    return (
        <div className="w-screen h-screen bg-gradient-to-r from-blue-500 to-teal-400 flex flex-col overflow-hidden">
            <Header />

            {/* Main Content */}
            <div className="flex flex-1 p-4 gap-4">
                {/* Question Section (80%) */}
                <div className="w-4/5 flex flex-col">
                    {/* Thanh đếm thời gian */}
                    <div className="w-full h-2 bg-gray-300 rounded-full mb-2">
                        <div className="h-full bg-red-500 rounded-full transition-all duration-1000"
                            style={{ width: `${(timeLeft / 30) * 100}%` }}></div>
                    </div>

                    {/* QuestionBox Component */}
                    <QuestionBox question={questionData.question} imageUrl={questionData.imageUrl} />

                    {/* Ô nhập câu trả lời */}
                    <div className="mt-2 w-full">
                        <input type="text"
                            className="w-full h-14 border border-gray-300 rounded-lg px-4 text-lg text-center"
                            placeholder="Type your answer..." />
                    </div>
                </div>

                {/* Sidebar (20%) */}
                <div className="w-1/5 flex flex-col">
                    <div className="bg-gray-300 text-center font-bold text-lg p-3 rounded-lg">ROUND 1</div>
                </div>
            </div>
        </div>
    );
};

export default Play;
