import Play from "../../layouts/Play";
import React, { useState } from "react";
import clsx from "clsx";

// Từ khóa chướng ngại vật
const obstacleWord = "THUOCTIEULUAN";

// Các từ khóa gợi ý
const hintWords = [
  { word: "GIÓ", position: [6, 1], direction: "vertical" },
  { word: "HẠT GIỐNG", position: [5, 2], direction: "vertical" },
  { word: "THUỐC", position: [5, 4], direction: "horizontal" },
  { word: "DREAM", position: [3, 3], direction: "horizontal" },
  { word: "CÚC", position: [4, 5], direction: "horizontal" },
  { word: "CHÂU KIẾT LUẬN", position: [4, 6], direction: "vertical" },
];

// Kích thước bảng
const gridSize = 10;

// Hàm tạo bảng crossword
const generateGrid = () => {
  const grid = Array(gridSize)
    .fill(null)
    .map(() => Array(gridSize).fill(""));

  // Điền từ khóa gợi ý vào bảng
  hintWords.forEach(({ word, position, direction }) => {
    const [startRow, startCol] = position;
    for (let i = 0; i < word.length; i++) {
      const row = direction === "vertical" ? startRow + i : startRow;
      const col = direction === "horizontal" ? startCol + i : startCol;
      grid[row][col] = word[i];
    }
  });

  return grid;
};

// Hàm lấy vị trí của các chữ trong từ khóa chướng ngại vật
const getObstacleLetterPositions = (grid: string[][]) => {
  const positions: Set<string> = new Set();
  grid.forEach((row, r) =>
    row.forEach((char, c) => {
      if (obstacleWord.includes(char)) {
        positions.add(`${r}-${c}`);
      }
    })
  );
  return positions;
};

const Crossword: React.FC = () => {
  const grid = generateGrid();
  const obstaclePositions = getObstacleLetterPositions(grid);

  // Trạng thái kiểm tra ô nào đã được mở
  const [revealedCells, setRevealedCells] = useState<Set<string>>(new Set());

  // Khi người chơi nhấn vào ô chữ
  const handleClick = (row: number, col: number) => {
    const newRevealed = new Set(revealedCells);
    newRevealed.add(`${row}-${col}`);
    setRevealedCells(newRevealed);
  };

  return (
    <div className="bg-black text-white p-6 flex flex-col items-center">
      <div className="grid grid-cols-10 gap-1 border-4 border-gray-600 p-2">
        {grid.map((row, r) =>
          row.map((char, c) => {
            if (!char) return <div key={`${r}-${c}`} className="w-8 h-8"></div>; // Ẩn ô trống

            const isObstacleLetter = obstaclePositions.has(`${r}-${c}`);
            const isRevealed = revealedCells.has(`${r}-${c}`);

            return (
              <div
                key={`${r}-${c}`}
                className={clsx(
                  "w-8 h-8 flex items-center justify-center border border-gray-400 text-lg font-bold cursor-pointer",
                  {
                    "bg-white text-black": isRevealed,
                    "bg-gray-700": !isRevealed,
                    "text-red-500": isObstacleLetter && isRevealed,
                  }
                )}
                onClick={() => handleClick(r, c)}
              >
                {isRevealed ? char : ""}
              </div>
            );
          })
        )}
      </div>

      {/* Danh sách từ gợi ý */}
      <div className="mt-4 text-lg">
        {hintWords.map(({ word }, index) => (
          <p key={index} className="text-blue-400 font-bold">
            {index + 1}. {word}
          </p>
        ))}
      </div>
    </div>
  );
};

function Round2() {
    return (
        <Play
            questionComponent= {<Crossword/>}
            
        />
    )
}

export default Round2
