import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { usePlayer } from '../../../context/playerContext';
import { useHost } from '../../../context/hostContext';
import roomService from '../../../services/room.service';
import authService from '../../../services/auth.service';
import { setRoomRules } from './service';

interface Room {
  roomId: string;
  isActive: boolean;
  mode: 'manual' | 'auto';
  roundScores: {
    round1: number[];
    round2: number[];
    round3: number;
    round4: number[];
  };
  round1Adaptive?: boolean;
  round2Adaptive?: boolean;
}

const SetupMatch: React.FC = () => {
  const navigate = useNavigate();
  const [currentRound, setCurrentRound] = useState<string>('');
  const [selectedTestName, setSelectedTestName] = useState<string>('');
  const [rooms, setRooms] = useState<Room[]>([]);
  const [showModal, setShowModal] = useState<boolean>(false);
  const [createdRoomId, setCreatedRoomId] = useState<string>('');
  const [testList] = useState<[]>(JSON.parse(localStorage.getItem('testList') || '[]'));
  const { setRoomId } = usePlayer();
  const { mode, setMode, rules, setRules } = useHost();

  const handleTestChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedTestName(event.target.value);
  };

  const handleStartClick = async (roomId: string, testName: string) => {
    setRoomId(roomId);
    const response = await authService.getAccessToken({ roomId });
    localStorage.setItem('accessToken', response.accessToken);
    setRoomRules(roomId, rules);
    navigate(`/host?round=1&roomId=${roomId}&testName=${testName}`);
  };

  useEffect(() => {
    const getRooms = async () => {
      const data = await roomService.getRoomByUser();
      setRooms(
        data.rooms.map((room: { roomId: string; isActive: boolean }) => ({
          ...room,
          mode: 'manual',
          roundScores: {
            round1: [15, 10, 10, 10],
            round2: [15, 10, 10, 10],
            round3: 10,
            round4: [10, 20, 30],
          },
          round1Adaptive: false,
          round2Adaptive: false,
        }))
      );
    };
    getRooms();
  }, []);

  const handleCreateRoom = async () => {
    const data = await roomService.createRoom(2);
    const newRoom: Room = {
      roomId: data.result.roomId,
      isActive: data.result.isActive,
      mode: 'manual',
      roundScores: {
        round1: [15, 10, 10, 10],
        round2: [15, 10, 10, 10],
        round3: 10,
        round4: [10, 20, 30],
      },
      round1Adaptive: false,
      round2Adaptive: false,
    };
    setRooms([...rooms, newRoom]);
    setCreatedRoomId(data.result.roomId);
    setShowModal(true);
  };

  const handleModeChange = (roomId: string, mode: 'manual' | 'auto') => {
    setRooms(rooms.map(room => room.roomId === roomId ? { ...room, mode } : room));
  };

  const handleScoreChange = (
    roomId: string,
    round: 'round1' | 'round2' | 'round3' | 'round4',
    index: number | null,
    value: number
  ) => {
    setRooms(prevRooms =>
      prevRooms.map(room => {
        if (room.roomId === roomId) {
          let newRoundScores;
          if (round === 'round3') {
            newRoundScores = { ...room.roundScores, round3: value };
          } else {
            const newScores = [...room.roundScores[round]];
            if (index !== null) newScores[index] = value;
            newRoundScores = { ...room.roundScores, [round]: newScores };
          }
          setRules(newRoundScores);
          return { ...room, roundScores: newRoundScores };
        }
        return room;
      })
    );
  };

  const handleAdaptiveChange = (roomId: string, round: 'round1' | 'round2', value: boolean) => {
    setRooms(rooms.map(room => (
      room.roomId === roomId ? { ...room, [`${round}Adaptive`]: value } : room
    )));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <div className="relative p-8">
      <div className="mb-8">
        <h2 className="text-3xl font-bold text-white mb-2">Thiết Lập Phòng Thi</h2>
        <p className="text-blue-200/80">Tạo và quản lý các phòng thi trực tuyến</p>
      </div>

      <div className="mb-8">
        <button
          onClick={handleCreateRoom}
          className="bg-gradient-to-r from-green-600 to-emerald-500 text-white px-6 py-3 rounded-lg hover:from-green-700 hover:to-emerald-600 font-medium transition-all duration-300 shadow-lg hover:shadow-green-500/25"
        >
          🏠 Tạo Phòng Mới
        </button>
      </div>

      <div className="bg-slate-700/50 backdrop-blur-sm border border-blue-400/30 rounded-xl overflow-hidden">
        <div className="p-6 border-b border-blue-400/30">
          <h3 className="text-xl font-semibold text-white">Danh Sách Phòng Thi</h3>
        </div>
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-slate-600/50">
              <tr>
                <th className="px-6 py-4 text-left text-blue-200 font-medium">Mã Phòng</th>
                <th className="px-6 py-4 text-left text-blue-200 font-medium">Trạng Thái</th>
                <th className="px-6 py-4 text-left text-blue-200 font-medium">Bộ Đề</th>
                <th className="px-6 py-4 text-left text-blue-200 font-medium">Chế Độ</th>
                <th className="px-6 py-4 text-left text-blue-200 font-medium">Hành Động</th>
              </tr>
            </thead>
            <tbody>
              {rooms.map(room => (
                <tr key={room.roomId} className="border-t border-blue-400/20 hover:bg-slate-600/30 transition-colors">
                  <td className="px-6 py-4 text-white font-mono">{room.roomId}</td>
                  <td className="px-6 py-4">
                    <span className={`px-3 py-1 rounded-full text-sm font-medium ${room.isActive ? 'bg-green-500/20 text-green-300 border border-green-500/30' : 'bg-red-500/20 text-red-300 border border-red-500/30'}`}>
                      {room.isActive ? 'Hoạt động' : 'Không hoạt động'}
                    </span>
                  </td>
                  <td className="px-6 py-4">
                    <select
                      value={selectedTestName}
                      onChange={handleTestChange}
                      className="bg-slate-600/50 border border-blue-400/30 rounded-lg px-3 py-2 text-white"
                    >
                      <option value="" disabled>-- Chọn bộ đề --</option>
                      {testList.map(test => (
                        <option key={test} value={test}>{test}</option>
                      ))}
                    </select>
                  </td>
                  <td className="px-6 py-4">
                    <div className="flex gap-4">
                      <label className="flex items-center gap-2 text-white">
                        <input
                          type="radio"
                          name={`mode-${room.roomId}`}
                          checked={room.mode === 'manual'}
                          onChange={() => { handleModeChange(room.roomId, 'manual'); setMode('manual'); }}
                        /> Thủ công
                      </label>
                      <label className="flex items-center gap-2 text-white">
                        <input
                          type="radio"
                          name={`mode-${room.roomId}`}
                          checked={room.mode === 'auto'}
                          onChange={() => { handleModeChange(room.roomId, 'auto'); setMode('auto'); }}
                        /> Tự động
                      </label>
                    </div>

                    {room.mode === 'auto' && (
                      <div className="mt-4 bg-slate-600/30 p-4 rounded-lg">
                        <h4 className="text-white font-medium mb-2">Cấu hình điểm</h4>
                        {/* Round 1 */}
                        <div className="mb-4">
                          <h5 className="text-blue-200 font-medium">Vòng 1</h5>
                          <div className="grid grid-cols-4 gap-2 mb-2">
                            {['Top 1', 'Top 2', 'Top 3', 'Top 4'].map((label, index) => (
                              <div key={index}>
                                <label className="text-blue-200 text-sm">{label}</label>
                                <input
                                  type="number"
                                  value={room.roundScores.round1[index]}
                                  onChange={e => handleScoreChange(room.roomId, 'round1', index, parseInt(e.target.value))}
                                  className="w-full px-2 py-1 bg-slate-600/50 border border-blue-400/30 rounded-lg text-white"
                                />
                              </div>
                            ))}
                          </div>
                          <div className="flex gap-4">
                            <label className="flex items-center gap-2 text-blue-200 text-sm">
                              <input
                                type="radio"
                                name={`round1Adaptive-${room.roomId}`}
                                checked={!room.round1Adaptive}
                                onChange={() => handleAdaptiveChange(room.roomId, 'round1', false)}
                              /> Mặc định
                            </label>
                            <label className="flex items-center gap-2 text-blue-200 text-sm">
                              <input
                                type="radio"
                                name={`round1Adaptive-${room.roomId}`}
                                checked={room.round1Adaptive}
                                onChange={() => handleAdaptiveChange(room.roomId, 'round1', true)}
                              /> Chấm theo số người trả lời đúng
                            </label>
                          </div>
                        </div>

                        {/* Round 2 */}
                        <div className="mb-4">
                          <h5 className="text-blue-200 font-medium">Vòng 2</h5>
                          <div className="grid grid-cols-4 gap-2 mb-2">
                            {['Top 1', 'Top 2', 'Top 3', 'Top 4'].map((label, index) => (
                              <div key={index}>
                                <label className="text-blue-200 text-sm">{label}</label>
                                <input
                                  type="number"
                                  value={room.roundScores.round2[index]}
                                  onChange={e => handleScoreChange(room.roomId, 'round2', index, parseInt(e.target.value))}
                                  className="w-full px-2 py-1 bg-slate-600/50 border border-blue-400/30 rounded-lg text-white"
                                />
                              </div>
                            ))}
                          </div>
                          <div className="flex gap-4">
                            <label className="flex items-center gap-2 text-blue-200 text-sm">
                              <input
                                type="radio"
                                name={`round2Adaptive-${room.roomId}`}
                                checked={!room.round2Adaptive}
                                onChange={() => handleAdaptiveChange(room.roomId, 'round2', false)}
                              /> Mặc định
                            </label>
                            <label className="flex items-center gap-2 text-blue-200 text-sm">
                              <input
                                type="radio"
                                name={`round2Adaptive-${room.roomId}`}
                                checked={room.round2Adaptive}
                                onChange={() => handleAdaptiveChange(room.roomId, 'round2', true)}
                              /> Chấm theo số người trả lời đúng
                            </label>
                          </div>
                        </div>

                        {/* Round 3 + 4 — Keep as you had them */}
                        {/* ... */}
                      </div>
                    )}
                  </td>
                  <td className="px-6 py-4">
                    <button
                      onClick={() => handleStartClick(room.roomId, selectedTestName)}
                      className="bg-gradient-to-r from-yellow-600 to-orange-500 text-white px-4 py-2 rounded-lg"
                    >
                      🚀 BẮT ĐẦU
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {showModal && (
        <div className="absolute inset-0 flex items-center justify-center bg-black/50">
          <div className="bg-slate-800 p-8 rounded-lg">
            <h3 className="text-white text-xl mb-4">Tạo phòng thành công!</h3>
            <p className="text-cyan-400 text-lg mb-4">{createdRoomId}</p>
            <button onClick={handleCloseModal} className="bg-blue-500 text-white px-4 py-2 rounded-lg">Đóng</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SetupMatch;
