import { useState } from "react";
import { getAuth, signInWithEmailAndPassword, User } from "firebase/auth";
import app from "./firebase-config"; // Import the initialized app

const auth = getAuth(app); // Pass the app instance to getAuth()

const useAuth = () => {
    const [user, setUser] = useState<User | null>(null); // Allow state to be either a User object or null
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState<string | null>(null); // Error can be a string or null

    const login = async (email: string, password: string) => {
        const auth = getAuth();
        try {
            const userCredential = await signInWithEmailAndPassword(auth, email, password);
            return userCredential.user; // Return the authenticated user
        } catch (err) {
            const error = err as Error; // Assert that 'err' is of type 'Error'
            console.error("Error:", error.message);
        }
    };

    const logout = async () => {
        const auth = getAuth();
        try {
            await auth.signOut();
            setUser(null); // Reset the user state to null after logging out
            console.log("User signed out");
        } catch (err: any) {
            console.error("Error signing out:", err.message);
        }
    };

    return { user, login, logout, loading, error };
};

export default useAuth;
