import Round2 from '../../../layouts/RoundBase/Round2';
import Host from '../../../layouts/Host/Host';
import QuestionBoxRound2 from '../../../layouts/RoundBase/Round2';
import { getQuestionByRound } from '../Test/service';
import React, { useEffect, useState, useRef } from 'react';
import { useSearchParams } from 'react-router-dom';
import { Question } from '../../../type';
import { listenToBuzzing } from '../../../services/firebaseServices';


const HostRound2: React.FC = () => {
    const [params] = useSearchParams()
    const roomId = params.get("roomId") || ""
    const testName = params.get("testName") || ""
    const [obstacleWord, setObstacleWord] = useState<string>("")
    const [buzzedPlayer, setBuzzedPlayer] = useState<string>("");
  const [showModal, setShowModal] = useState(false); // State for modal visibility
    const [hintWords, setHintWords] = useState<string[]>([])
    const isMounted = useRef(false)
    useEffect(() => {

        if(!isMounted.current) {
          isMounted.current = true
          return
        }
        
        const unsubscribeBuzzing = listenToBuzzing(roomId, (playerName) => {
          if (playerName) {
            setBuzzedPlayer(playerName);
            console.log("playerName", typeof playerName);
            
            console.log(playerName,"đã bấm chuông")
            setShowModal(true); // Show modal when a player buzzes
          }
        });
    
        return () => {
          unsubscribeBuzzing();
        };
      }, [roomId]);
    useEffect(() => {
        const getSecondRoundQuestion = async () => {
            const result = await getQuestionByRound(testName, "2", roomId)
            console.log("round 2 data", result)
            const sortedResult = [...result].sort((a, b) => a.stt - b.stt);
            console.log("sorted result", sortedResult);
            setObstacleWord(sortedResult[6].answer.toUpperCase().replace(/\s+/g, ""))
            const updatedHintWords = []
            for (let i = 0; i < sortedResult.length - 1; i++) {
                const word = sortedResult[i].answer.toUpperCase().replace(/\s+/g, "");
                updatedHintWords.push(word);

                // Old style (shows full array due to reference)
                console.log("updatedHintWords", updatedHintWords);

                // Better: shows actual content at this step
                console.log("updatedHintWords (snapshot)", [...updatedHintWords]);
            }
            //const hintWords = result.slice(0, result.length - 1).map((item: any) => item.answer.toUpperCase().replace(/\s+/g, ""));
            console.log("hintWords", [...updatedHintWords])
            setHintWords([...updatedHintWords]);

        }

        getSecondRoundQuestion()

    }, [])
    return (
        <Host
            QuestionComponent={<QuestionBoxRound2 obstacleWord={obstacleWord} hintWordArray={hintWords} isHost={true} />}
        />

    )
};

export default HostRound2;
