import React, { useEffect, useState, useRef } from 'react'
import { usePlayer } from '../context/playerContext';
import { User, Answer, Score } from '../type';
import { listenToBroadcastedAnswer, listenToOpenBuzz } from '../services/firebaseServices';
import { useSearchParams } from 'react-router-dom';
import { buzzing } from './services';
import { closeBuzz } from './services';
import { useTimeStart } from '../context/timeListenerContext';
import { submitAnswer } from '../layouts/services';

const PlayerAnswer: React.FC = () => {
    const { playersArray, playerFlashes, setPlayerFlashes, roomId, triggerPlayerFlash, scoreList, setScoreList, position, currentPlayerName } = usePlayer()
    const [answerList, setAnswerList] = useState<Answer[]>([])
    const spots = [1, 2, 3, 4]
    const [searchParams] = useSearchParams()
    const round = searchParams.get("round") || "1"

    const isInitialMount = useRef(true);
    const playerAnswerRef = useRef("");
    const { timeLeft, startTimer } = useTimeStart();
    useEffect(() => {
        if (isInitialMount.current) {
            isInitialMount.current = false; // Skip the initial mount
            return;
        }

        // Start timer when selectedTopic changes
        startTimer(30);

        // Side effects based on timer reaching 0
    }, [startTimer]);

    useEffect(() => {
        console.log("timeLeft", timeLeft);

        if (timeLeft === 0) {
            // When timer runs out, do your clean up / game logic:
            submitAnswer(roomId, playerAnswerRef.current, position)
            // If you want to reset timer, call startTimer again here or leave stopped
        }
    }, [timeLeft]);
    useEffect(() => {
        console.log("timeLeft on play", timeLeft)
    }, [timeLeft]);
 
    const [isButtonEnabled, setIsButtonEnabled] = useState(round === "2")
        const handleKeyDown = (event: React.KeyboardEvent<HTMLInputElement>) => {
        if (event.key === "Enter") {
            const inputElement = event.target as HTMLInputElement; // Type assertion
            console.log("inputElement.value", inputElement.value)
            playerAnswerRef.current = inputElement.value
            inputElement.value = ""; // Clears input field
        }
    };

    const handleBuzz = async () => {
        console.log("currentPlayerName",currentPlayerName);
        
        await buzzing(roomId, currentPlayerName, position)
    }

    useEffect(() => {
        const unsubscribeBroadcastedAnswer = listenToBroadcastedAnswer(roomId, (answerList) => {
            setAnswerList(answerList)
            console.log("answerList", answerList)
        })

        return () => {
            unsubscribeBroadcastedAnswer();
        };
    }, [roomId]);

    useEffect(() => {
        const unsubscribeOpenBuzz = listenToOpenBuzz(roomId, (buzz) => {
            console.log("buzz", buzz);
            
            if (round === "4" && buzz==="open") {
                setIsButtonEnabled(true)
                const timeoutId = setTimeout(() => {
                    setIsButtonEnabled(false)
                    closeBuzz(roomId)
                }, 4000)
                return () => {
                    
                    clearTimeout(timeoutId)
                }
            }
        })

        return () => {
            unsubscribeOpenBuzz();
        };
    }, [roomId, round]);

    useEffect(() => {
        if (playerFlashes && playerFlashes.length > 0 && playerFlashes.some((p: Score) => p.flashColor)) {
            const timeoutId = setTimeout(() => {
                setPlayerFlashes((prevFlashes: Score[]) =>
                    prevFlashes.map((player) => ({ ...player, flashColor: undefined }))
                );
            }, 3000);

            return () => clearTimeout(timeoutId);
        }
    }, [playerFlashes, setPlayerFlashes]);

    return (
        <>
            <button
                onClick={() => {
                    alert('buzzed')
                    handleBuzz()
                }}
                className={`p-2 flex-1 rounded-md text-white ${
                    isButtonEnabled ? 'bg-green-500' : 'bg-gray-500 cursor-not-allowed'
                }`}
                disabled={!isButtonEnabled}
            >
                {round === "2" ? "Trả lời CNV" : round === "4" ? "Giành quyền trả lời" : ""}
            </button>
            <div className="flex justify-around mt-4">
                {
                    spots.map((spotNumber: number) => {
                        const storedPlayers = localStorage.getItem("playerList");
                        { console.log("storedPlayers", storedPlayers) }
                        { console.log("playersArray", playersArray) }
                        const array = playersArray !== null
                            ? playersArray
                            : (storedPlayers ? JSON.parse(storedPlayers) : []);
                        { console.log("array", array) }
                        const player = array.find((p: User) => parseInt(p.stt) === spotNumber);

                        const answer = Array.isArray(answerList) && answerList.length !== 0 
                            ? answerList.find((answer: Answer) => parseInt(answer.stt) === spotNumber) 
                            : null
                        console.log("answer in spot", answer);
                        
                        if (player) {
                            return (
                                <>
                                <div className="mt-2 w-full">
                            <input
                                type="text"
                                className="w-full h-14 border border-gray-300 rounded-lg px-4 text-lg text-center"
                                placeholder="Type your answer..."
                                onKeyDown={handleKeyDown}
                            // value={playerAnswer} 
                            // onChange={(e) => setPlayerAnswer(e.target.value)}
                            />
                            <p className="mt-2 text-lg">{playerAnswerRef.current && `Your answer: ${playerAnswerRef.current}`}</p>
                        </div>

                                <div key={spotNumber} className={`flex flex-col items-center 
                                ${playerFlashes[spotNumber - 1] ? playerFlashes[spotNumber - 1].flashColor : ""}`}>
                                    <img
                                        src={player.avatar}
                                        alt="Player"
                                        className="w-16 h-16 rounded-full"
                                    />
                                    <p className="text-white mt-2">
                                        {answer && answer.answer}
                                    </p>
                                    <p className="text-white">{`player_${player.stt}: ${player.userName}`}</p>
                                </div>
                                </>
                        
                            )
                        }
                    })
                }
            </div>
        </>
    )
}

export default PlayerAnswer
