import React, { useState } from 'react';
import Header from './Header';

function Play() {
    const [isChatOpen, setIsChatOpen] = useState(false);

    return (
        <div className="w-screen h-screen bg-gradient-to-r from-blue-500 to-teal-400 flex flex-col">
            {/* Header */}
            <Header />

            {/* Main Game Section */}
            <div className="flex flex-1 p-4">
                {/* Question Box (M·ªü r·ªông k√≠ch th∆∞·ªõc) */}
                <div className="w-4/5 p-4">
                    <div className="w-full h-[350px] bg-white rounded-lg shadow-md"></div>
                    {/* Answer Input (Di chuy·ªÉn xu·ªëng d∆∞·ªõi) */}
                    <div className="bg-white mt-6 p-4 rounded-lg shadow-md">
                        <p className="text-sm font-bold">Your Answer</p>
                        <input
                            type="text"
                            className="w-full h-12 mt-2 border border-gray-300 rounded-lg px-2 text-lg"
                            placeholder="Type your answer..."
                        />
                    </div>
                    {/* Player Scores */}
                    <div className="flex justify-around mt-6">
                        {Array(4)
                            .fill(0)
                            .map((_, index) => (
                                <div key={index} className="flex flex-col items-center">
                                    <img
                                        src="https://via.placeholder.com/60"
                                        alt="Player"
                                        className="w-16 h-16 rounded-full"
                                    />
                                    <p className="text-white mt-2">Score: <b>10</b></p>
                                    <p className="text-white">Username</p>
                                </div>
                            ))}
                    </div>

                    
                </div>

                {/* Sidebar */}
                <div className="w-1/5 p-4">
                    {/* Round Indicator */}
                    <div className="bg-gray-300 text-center font-bold text-lg p-3 rounded-lg">
                        ROUND 1
                    </div>

                    {/* Player Scores */}
                    <div className="bg-white mt-4 p-4 rounded-lg shadow-md">
                        {Array(3).fill(0).map((_, index) => (
                            <div key={index} className="mb-4">
                                <p className="text-sm">Username</p>
                                <div className="w-full bg-gray-200 text-center py-2 rounded-lg">
                                    Score: {Math.floor(Math.random() * 20)}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Chat Toggle Button */}
            <button
                className="fixed bottom-6 right-6 bg-gray-800 text-white w-12 h-12 flex items-center justify-center rounded-full shadow-lg"
                onClick={() => setIsChatOpen(!isChatOpen)}
            >
                {isChatOpen ? "‚úñ" : "üí¨"}
            </button>

            {/* Chat Box */}
            {isChatOpen && (
                <div className="fixed bottom-20 right-6 w-80 h-60 bg-white shadow-lg rounded-lg p-4">
                    <p className="text-sm font-bold">Chat</p>
                    <div className="h-40 overflow-y-auto bg-gray-100 p-2 rounded-lg">
                        <p className="text-xs">User1: Hello!</p>
                        <p className="text-xs text-right">You: Hi there!</p>
                    </div>
                    <input
                        type="text"
                        className="w-full h-10 mt-2 border border-gray-300 rounded-lg px-2"
                        placeholder="Type a message..."
                    />
                </div>
            )}
        </div>
    );
}

export default Play;
