import axios from "axios";

class Http {
    baseUrl: string | undefined

    constructor() {
        this.baseUrl = "http://127.0.0.1:8000"
    }

    private async request<T>(
        method: string,
        endpoint: string,
        withCredentials: boolean,
        data?: any
    ) {
        try {
            const response = await axios.request<T>({
                method,
                url: `${this.baseUrl}/${endpoint}`,
                data,
                withCredentials
            })

            return response.data
        } catch (error) {
            throw error
        }
    }

    async get(endpoint: string, isCreadential: boolean) {
        return this.request("GET", endpoint, isCreadential)
    }

    async post(endpoint: string, isCreadential: boolean, data: any) {
        return this.request("POST", endpoint, isCreadential, data)
    }

    async put(endpoint: string, isCreadential: boolean, data: any) {
        return this.request("PUT", endpoint, isCreadential, data )
    }

    async delete(endpoint: string, isCreadential: boolean, data: any) {
        return this.request("DELETE", endpoint, isCreadential, data)
    }
}

const http = new Http()
export default http
