import React, { useState, useEffect } from 'react';
import banner from "../../assets/banner.jpg";
import card from "../../assets/card.jpg";
import ending from "../../assets/ending.jpg";

const Login = () => {
  const images = [banner, card, ending];

  const messages = [
    "Chào mừng đến với Hành Trình Magellan",
    "Khám phá thế giới cùng Magellan",
    "Hành trình bắt đầu từ đây",
  ];

  // State để quản lý index của ảnh và text hiện tại
  const [currentIndex, setCurrentIndex] = useState(0);

  // Chuyển đổi ảnh và text mỗi 2 giây để tạo hiệu ứng liên tục
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % messages.length);
    }, 2000); // 2 giây (tổng thời gian animation chia cho số lượng ảnh/text)
    return () => clearInterval(interval);
  }, [messages.length]);

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-b from-gray-100 to-gray-300">
      <div className="w-full max-w-3xl bg-white rounded-xl shadow-lg flex border-2 border-gray-300">
        {/* Phần chào mừng với animation */}
        <div className="w-1/2 relative overflow-hidden rounded-r-[4rem]">
          {/* Phần ảnh nền luân phiên */}
          {images.map((image, index) => (
            <div
              key={index}
              className="absolute inset-0 bg-cover animate-imageSlide"
              style={{
                backgroundImage: `url(${image})`,
                animationDelay: `${index * 2}s`, // Mỗi ảnh trượt sau 2 giây
              }}
            />
          ))}
          {/* Phần chữ chạy liên tục */}
          <div className="relative z-10 flex flex-col items-center justify-center h-full p-8 text-white">
            {messages.map((message, index) => (
              <h2
                key={index}
                className="text-3xl font-bold mb-4 absolute animate-textSlide"
                style={{
                  animationDelay: `${index * 2}s`, // Mỗi text trượt sau 2 giây
                }}
              >
                {message}
              </h2>
            ))}
          </div>
        </div>

        {/* Phần form đăng nhập */}
        <div className="w-1/2 p-8">
          <h2 className="text-3xl font-bold text-center mb-8">Login</h2>
          <form>
            <div className="mb-6 relative">
              <label className="block text-gray-700 text-lg font-bold mb-2" htmlFor="username">
                Username
              </label>
              <input
                className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-600 text-lg bg-gray-200"
                type="text"
                id="username"
                placeholder="Enter your username"
              />
              <span className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-500">👤</span>
            </div>
            <div className="mb-6 relative">
              <label className="block text-gray-700 text-lg font-bold mb-2" htmlFor="password">
                Password
              </label>
              <input
                className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-600 text-lg bg-gray-200"
                type="password"
                id="password"
                placeholder="Enter your password"
              />
              <span className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-500">🔒</span>
            </div>
            <div className="mb-6">
              <a href="#" className="block text-lg text-blue-600 hover:underline mb-4">Forgot Password?</a>
              <button
                type="submit"
                className="w-full px-4 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 text-lg"
              >
                Login
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
