import Play from "../Play";
import React, { useState, useEffect, useRef } from "react";
import { renderGrid } from "./utils";
import { usePlayer } from "../../context/playerContext";
import { setSelectedRow, setCorrectRow, setIncorectRow } from "../../components/services";
import { listenToCorrectRow, listenToIncorrectRow, listenToSelectRow } from "../../services/firebaseServices";
import { useSearchParams } from "react-router-dom";


interface HintWord {
  word: string;
  x: number;
  y: number;
  direction: "horizontal" | "vertical";
}

interface ObstacleQuestionBoxProps {
  obstacleWord?: string;
  hintWordArray?: string[];
  isHost?: boolean;
  initialGrid?: string[][];
}

const mainKeyword = "BÒCÔNGANH";

const QuestionBoxRound2: React.FC<ObstacleQuestionBoxProps> = ({
  obstacleWord = mainKeyword,
  hintWordArray,
  initialGrid,
  isHost = false,
}) => {
  console.log("hintWordArray", typeof hintWordArray);
  console.log("obstacle", obstacleWord);

  const [searchParams] = useSearchParams();
  const { setInitialGrid } = usePlayer();
  const roomId = searchParams.get("roomId") || "";
  const GRID_SIZE = 20;

  const generateEmptyGrid = () => {
    return Array(GRID_SIZE)
      .fill(null)
      .map(() => Array(GRID_SIZE).fill(""));
  };

  const [grid, setGrid] = useState<string[][]>(generateEmptyGrid());
  const [hintWords, setHintWords] = useState<HintWord[]>([]);
  const [cellStyles, setCellStyles] = useState<
    Record<string, { background: string; textColor: string }>
  >({}); // Tracks background and text styles
  const [menu, setMenu] = useState<{
    visible: boolean;
    rowIndex?: number;
    colIndex?: number;
  }>({ visible: false });
  const [hintWordsLength, setHintWordsLength] = useState<number[]>([]);

  const menuRef = useRef<HTMLDivElement>(null);

  const revealCells = (
    rowIndex: number,
    colIndex: number,
    action: "open" | "correct" | "incorrect",
    hintWordNumber?: string
  ) => {
    if (!isHost) return;

    const hintWordIndex = hintWordNumber ? parseInt(hintWordNumber) - 1 : -1;
    const hintWord = hintWords.find(
      (word) =>
        (word.y === rowIndex && word.direction === "horizontal") ||
        (word.x === colIndex && word.direction === "vertical")
    );

    if (!hintWord) return;

    const isRow = hintWord.direction === "horizontal";
    const wordLength =
      hintWordIndex >= 0 && hintWordArray ? hintWordArray[hintWordIndex].length : hintWord.word.length;
    const startIndex = isRow ? colIndex + 1 : rowIndex + 1;

    setCellStyles((prev) => {
      const newStyles = { ...prev };
      if (isRow) {
        for (let col = startIndex; col < startIndex + wordLength; col++) {
          const key = `${rowIndex}-${col}`;
          // Skip number cells to preserve their appearance
          if (!grid[rowIndex][col].includes("number")) {
            if (action === "open") {
              newStyles[key] = { background: "bg-yellow-200", textColor: "text-transparent" };
              if (hintWordNumber)
                setSelectedRow(roomId, hintWordNumber, true,wordLength)
            } else if (action === "correct") {
              newStyles[key] = { background: "bg-yellow-200", textColor: "text-black" };
              let indexInTarget = 0
              if (hintWordArray)
                for (let i = 0; i < hintWordArray[hintWordIndex].length; i++) {
                  const char = hintWordArray[hintWordIndex][i];
                  indexInTarget = obstacleWord.indexOf(char);

                  if (indexInTarget !== -1) {
                    console.log(`First matching character: ${char}`);
                    console.log(`Index in "${obstacleWord}": ${indexInTarget}`);
                    break;
                  }
                }
              if (hintWordArray && hintWordNumber)
                setCorrectRow(roomId, hintWordNumber, hintWordArray[hintWordIndex], indexInTarget.toString(), true, wordLength)
            } else if (action === "incorrect") {
              newStyles[key] = { background: "bg-gray-400", textColor: "text-transparent" };
              if (hintWordNumber)
                setIncorectRow(roomId, hintWordNumber, true, wordLength)
            }
          }
        }
      } else {
        for (let row = startIndex; row < startIndex + wordLength; row++) {
          const key = `${row}-${colIndex}`;
          // Skip number cells to preserve their appearance
          if (!grid[row][colIndex].includes("number")) {
            if (action === "open") {
              newStyles[key] = { background: "bg-yellow-200", textColor: "text-transparent" };
              if (hintWordNumber)
                setSelectedRow(roomId, hintWordNumber, false, wordLength)
            } else if (action === "correct") {
              newStyles[key] = { background: "bg-yellow-200", textColor: "text-black" };
              let indexInTarget = 0
              if (hintWordArray)
                for (let i = 0; i < hintWordArray[hintWordIndex].length; i++) {
                  const char = hintWordArray[hintWordIndex][i];
                  indexInTarget = obstacleWord.indexOf(char);

                  if (indexInTarget !== -1) {
                    console.log(`First matching character: ${char}`);
                    console.log(`Index in "${obstacleWord}": ${indexInTarget}`);
                    break;
                  }
                }
              if (hintWordArray && hintWordNumber)
                setCorrectRow(roomId, hintWordNumber, hintWordArray[hintWordIndex], indexInTarget.toString(), false, wordLength)
            } else if (action === "incorrect") {
              newStyles[key] = { background: "bg-gray-400", textColor: "text-transparent" };
              if (hintWordNumber)
                setIncorectRow(roomId, hintWordNumber, false, wordLength)
            }
          }
        }
      }
      return newStyles;
    });

    // if (action === "correct" || action === "incorrect") {
    //   setSelectedRow(roomId, isRow ? rowIndex.toString() : colIndex.toString());
    // }
  };


  const revealCellsForPlayer = (
    rowIndex: number,
    colIndex: number,
    action: "open" | "incorrect" | "correct",
    selectedRowNumber: string,
    isRow: boolean,
    wordLength?: number, // For open and incorrect
    correctAnswer?: string // For correct
  ) => {
    if (isHost) return; // Ensure this runs only for players
  
    // Determine word length
    const length = action === "correct" ? correctAnswer?.length || 0 : wordLength || 0;
  
    if (length === 0) {
      console.warn(`No valid word length for row ${selectedRowNumber}`);
      return;
    }
  
    setCellStyles((prev) => {
      const newStyles = { ...prev };
      if (isRow) {
        // Horizontal: style cells from colIndex + 1 to colIndex + length
        for (let col = colIndex + 1; col <= colIndex + length; col++) {
          const key = `${rowIndex}-${col}`;
          // Skip empty and number cells
          if (grid[rowIndex][col] !== "" && !grid[rowIndex][col]?.includes("number")) {
            if (action === "open") {
              newStyles[key] = { background: "bg-yellow-200", textColor: "text-transparent" };
            } else if (action === "incorrect") {
              newStyles[key] = { background: "bg-gray-400", textColor: "text-transparent" };
            } else if (action === "correct") {
              newStyles[key] = { background: "bg-yellow-200", textColor: "text-black" };
            }
          }
        }
      } else {
        // Vertical: style cells from rowIndex + 1 to rowIndex + length
        for (let row = rowIndex + 1; row <= rowIndex + length; row++) {
          const key = `${row}-${colIndex}`;
          // Skip empty and number cells
          if (grid[row][colIndex] !== "" && !grid[row][colIndex]?.includes("number")) {
            if (action === "open") {
              newStyles[key] = { background: "bg-yellow-200", textColor: "text-transparent" };
            } else if (action === "incorrect") {
              newStyles[key] = { background: "bg-gray-400", textColor: "text-transparent" };
            } else if (action === "correct") {
              newStyles[key] = { background: "bg-yellow-200", textColor: "text-black" };
            }
          }
        }
      }
      return newStyles;
    });
  
    // For "correct", update grid to show the actual word
    if (action === "correct" && correctAnswer) {
      setGrid((prevGrid) => {
        const newGrid = prevGrid.map((row) => [...row]);
        if (isRow) {
          for (let col = colIndex + 1, i = 0; col <= colIndex + length && i < correctAnswer.length; col++, i++) {
            newGrid[rowIndex][col] = correctAnswer[i];
          }
        } else {
          for (let row = rowIndex + 1, i = 0; row <= rowIndex + length && i < correctAnswer.length; row++, i++) {
            newGrid[row][colIndex] = correctAnswer[i];
          }
        }
        return newGrid;
      });
    }
  };
  useEffect(() => {
    if (hintWordArray) {
      hintWordArray.forEach((word, index) => {
        console.log(`Word ${index}: '${word}' has length: ${word.length}`);
        console.log([...word]);
      });
      const lengthArray = hintWordArray.map((word) => word.length);
      setHintWordsLength(lengthArray);
    }
  }, [hintWordArray]);

  // Listen for "open" (select) signal
useEffect(() => {
  const unsubscribeSelect = listenToSelectRow(roomId, (selectedRowNumber, isRow, wordLength) => {
    if (!initialGrid || !grid) return;

    let rowIndex = -1;
    let colIndex = -1;
    for (let row = 0; row < grid.length; row++) {
      for (let col = 0; col < grid[row].length; col++) {
        if (grid[row][col] === `number${selectedRowNumber}`) {
          rowIndex = row;
          colIndex = col;
          break;
        }
      }
      if (rowIndex !== -1) break;
    }

    if (rowIndex === -1 || colIndex === -1) {
      console.warn(`Number cell number${selectedRowNumber} not found`);
      return;
    }

    revealCellsForPlayer(rowIndex, colIndex, "open", selectedRowNumber, isRow, wordLength);
  });

  return () => {
    unsubscribeSelect();
  };
}, [initialGrid, grid, roomId]);

// Listen for "incorrect" signal
useEffect(() => {
  const unsubscribeIncorrect = listenToIncorrectRow(roomId, (selectedRowNumber, isRow, wordLength) => {
    if (!initialGrid || !grid) return;

    let rowIndex = -1;
    let colIndex = -1;
    for (let row = 0; row < grid.length; row++) {
      for (let col = 0; col < grid[row].length; col++) {
        if (grid[row][col] === `number${selectedRowNumber}`) {
          rowIndex = row;
          colIndex = col;
          break;
        }
      }
      if (rowIndex !== -1) break;
    }

    if (rowIndex === -1 || colIndex === -1) {
      console.warn(`Number cell number${selectedRowNumber} not found`);
      return;
    }

    revealCellsForPlayer(rowIndex, colIndex, "incorrect", selectedRowNumber, isRow, wordLength);
  });

  return () => {
    unsubscribeIncorrect();
  };
}, [initialGrid, grid, roomId]);

// Listen for "correct" signal
useEffect(() => {
  const unsubscribeCorrect = listenToCorrectRow(
    roomId,
    (data) => {
      if (!initialGrid || !grid) return;

      let rowIndex = -1;
      let colIndex = -1;
      for (let row = 0; row < grid.length; row++) {
        for (let col = 0; col < grid[row].length; col++) {
          if (grid[row][col] === `number${data.selected_row_number}`) {
            rowIndex = row;
            colIndex = col;
            break;
          }
        }
        if (rowIndex !== -1) break;
      }

      if (rowIndex === -1 || colIndex === -1) {
        console.warn(`Number cell number${data.selected_row_number} not found`);
        return;
      }

      revealCellsForPlayer(rowIndex, colIndex, "correct", data.selected_row_number, data.is_row, undefined, correctAnswer);
    }
  );

  return () => {
    unsubscribeCorrect();
  };
}, [initialGrid, grid, roomId]);
  // Initialize grid
  useEffect(() => {
    if (isHost) {
      const wordList = ["GIÓ", "HẠTGIỐNG", "DREAM", "THUỐC", "CÚC", "CHÂUKIỆTLUÂN"];
      console.log("wordList", wordList);
      console.log("hintWordArray", hintWordArray);
      if (hintWordArray && obstacleWord) {
        const { randomHintWords, newGrid } = renderGrid(hintWordArray, obstacleWord, GRID_SIZE);
        console.log("randomHintWords", randomHintWords);
        console.log("newGrid", newGrid);
        setHintWords(randomHintWords);

        const unrevealGrid = newGrid.map(row =>
          row.map(cell => {
            if (typeof cell === 'string' && cell.startsWith('number')) {
              return cell; // Keep elements starting with "number"
            }
            return cell === '' ? "" : "1"; // 0 for empty string, 1 for non-empty
          })
        );
        setInitialGrid(unrevealGrid);
        setGrid(newGrid);
      }
    } else {
      if (initialGrid) {
        setGrid(initialGrid);
      }
    }
  }, [isHost, hintWordArray, obstacleWord, initialGrid, setInitialGrid]);

  // Reveal cells in a row or column


  // Handle number click to show menu
  const handleNumberClick = (rowIndex: number, colIndex: number) => {
    if (!isHost) return;
    setMenu({
      visible: true,
      rowIndex,
      colIndex,
    });
  };

  // Handle menu actions
  const handleMenuAction = (
    action: "open" | "correct" | "incorrect",
    rowIndex: number,
    colIndex: number,
    hintWordNumber: string
  ) => {
    revealCells(rowIndex, colIndex, action, hintWordNumber);
    setMenu({ visible: false });
  };

  // Handle cell click to reveal
  const handleCellClick = (rowIndex: number, colIndex: number) => {
    if (!isHost) return;
    revealCells(rowIndex, colIndex, "open");
  };

  // Close menu on outside click
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (menuRef.current && !menuRef.current.contains(event.target as Node)) {
        setMenu({ visible: false });
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div className="flex flex-col items-center bg-white rounded-lg shadow-md p-6 relative">
      <div className="text-gray-700 text-xl font-semibold text-center mb-4 max-w-[90%]">
        Trong các giải đấu thể thao, những đội hay vận động viên mạnh sẽ được xếp vào cùng một hoặc nhiều nhóm trong khi bốc thăm để tránh việc gặp nhau sớm. Tên gọi chung của các nhóm vận động viên này là gì?
      </div>
      <div className="grid grid-cols-[repeat(20,40px)] grid-rows-[repeat(20,40px)] gap-1 max-h-[400px] overflow-y-scroll">
        {grid.map((row, rowIndex) => (
          <React.Fragment key={rowIndex}>
            {row.map((cell, colIndex) => {
              const cellKey = `${rowIndex}-${colIndex}`;
              const cellStyle = cellStyles[cellKey] || {
                background: cell === "" ? "bg-white" : "bg-gray-50",
                textColor: cell.includes("number") ? "text-blue-500" : "text-transparent",
              };

              const showMenu =
                menu.visible &&
                menu.rowIndex === rowIndex &&
                menu.colIndex === colIndex &&
                cell.includes("number");

              return (
                <div className="relative flex items-center" key={colIndex}>
                  <div
                    className={`w-10 h-10 flex items-center justify-center text-lg font-semibold select-none
                      ${cell.includes("number") ? "text-blue-500 border-none" : ""}
                      ${cell.includes("number") ? "" : cellStyle.background}
                      ${cell.includes("number") ? "text-blue-500" : cellStyle.textColor}
                      ${obstacleWord?.includes(cell) &&
                        cellStyle.textColor === "text-black" &&
                        !cell.includes("number") &&
                        isNaN(Number(cell))
                        ? "font-bold text-red-500"
                        : ""}
                    `}
                    onClick={() => {
                      if (isHost) {
                        if (cell.includes("number")) {
                          handleNumberClick(rowIndex, colIndex);
                        } else {
                          handleCellClick(rowIndex, colIndex);
                        }
                      }
                    }}
                    style={{
                      cursor:
                        isHost &&
                          (cell.includes("number") ||
                            hintWords.some((word) => word.y === rowIndex || word.x === colIndex))
                          ? "pointer"
                          : "default",
                    }}
                  >
                    {cell.includes("number") ? cell.replace("number", "").trim() : cell}
                  </div>
                  {showMenu && (
                    <div
                      ref={menuRef}
                      className="absolute left-12 top-1/2 transform -translate-y-1/2 flex space-x-2 bg-white border border-gray-300 rounded shadow-lg p-1 z-10"
                    >
                      <button
                        className="px-2 py-1 text-sm bg-blue-500 text-white rounded hover:bg-blue-600"
                        onClick={() =>
                          handleMenuAction("open", rowIndex, colIndex, cell.replace("number", ""))
                        }
                      >
                        SELECT
                      </button>
                      <button
                        className="px-2 py-1 text-sm bg-green-500 text-white rounded hover:bg-green-600"
                        onClick={() =>
                          handleMenuAction("correct", rowIndex, colIndex, cell.replace("number", ""))
                        }
                      >
                        Correct
                      </button>
                      <button
                        className="px-2 py-1 text-sm bg-red-500 text-white rounded hover:bg-red-600"
                        onClick={() =>
                          handleMenuAction("incorrect", rowIndex, colIndex, cell.replace("number", ""))
                        }
                      >
                        Incorrect
                      </button>
                    </div>
                  )}
                </div>
              );
            })}
          </React.Fragment>
        ))}
      </div>
    </div>
  );
};

export default QuestionBoxRound2;
