    const wordBank = ["GIÓ", "HẠT GIỐNG", "DREAM", "THUỐC", "CÚC", "CHÂU KIỆT LUÂN"]
      function PopulateBoard(){


      for(var i=0,isOk=true,len=wordBank.length; i<len && isOk; i++){
        isOk = AddWordToBoard();
      }
      return isOk;
    }




    function findWordIdx(givenWord:string){
      givenWord = givenWord.substring(2, givenWord.length-1);
      for(var i = 0, len = wordArr.length; i < len; i++){
        if(givenWord == wordArr[i]) return i+1;
      }
    }

    // TODO: Clean this guy up
    function AddWordToBoard(){
      var i, len, curIndex, curWord, curChar, curMatch, testWord, testChar,
          minMatchDiff = 9999, curMatchDiff;

      if(wordsActive.length < 1){
        curIndex = 0;
        for(i = 0, len = wordBank.length; i < len; i++){
          if (wordBank[i].totalMatches < wordBank[curIndex].totalMatches){
            curIndex = i;
          }
        }
        wordBank[curIndex].successfulMatches = [{x:12,y:12,dir:0}];
      }
      else{
        curIndex = -1;

        for(i = 0, len = wordBank.length; i < len; i++){
          curWord = wordBank[i];
          curWord.effectiveMatches = 0;
          curWord.successfulMatches = [];
          for(var j = 0, lenJ = curWord.char.length; j < lenJ; j++){
            if(j == 1) continue; // skip pos
            curChar = curWord.char[j];
            for (var k = 0, lenK = wordsActive.length; k < lenK; k++){
              testWord = wordsActive[k];
              for (var l = 0, lenL = testWord.char.length; l < lenL; l++){
                if(l == 1) continue; // skip pos
                testChar = testWord.char[l];
                if (curChar === testChar){
                  curWord.effectiveMatches++;

                  var curCross = {x:testWord.x,y:testWord.y,dir:0};
                  if(testWord.dir === 0){
                    curCross.dir = 1;
                    curCross.x += l;
                    curCross.y -= j;
                  }
                  else{
                    curCross.dir = 0;
                    curCross.y += l;
                    curCross.x -= j;
                  }

                  var isMatch = true;

                  for(var m = -1, lenM = curWord.char.length + 1; m < lenM; m++){
                    var crossVal = [];
                    if (m !== j){
                      if (curCross.dir === 0){
                        var xIndex = curCross.x + m;

                        if (xIndex < 0 || xIndex > board.length){
                          isMatch = false;
                          break;
                        }

                        crossVal.push(board[xIndex][curCross.y]);
                        crossVal.push(board[xIndex][curCross.y + 1]);
                        crossVal.push(board[xIndex][curCross.y - 1]);
                      }
                      else{
                        var yIndex = curCross.y + m;

                        if (yIndex < 0 || yIndex > board[curCross.x].length){
                          isMatch = false;
                          break;
                        }

                        crossVal.push(board[curCross.x][yIndex]);
                        crossVal.push(board[curCross.x + 1][yIndex]);
                        crossVal.push(board[curCross.x - 1][yIndex]);
                      }

                      if(m > -1 && m < lenM-1){
                        if (crossVal[0] !== curWord.char[m]){
                          if (crossVal[0] !== null){
                            isMatch = false;
                            break;
                          }
                          else if (crossVal[1] !== null){
                            isMatch = false;
                            break;
                          }
                          else if (crossVal[2] !== null){
                            isMatch = false;
                            break;
                          }
                        }
                      }
                      else if (crossVal[0] !== null){
                        isMatch = false;
                        break;
                      }
