import React from 'react'
import { useState, useEffect } from "react"

import banner from "../../assets/banner.jpg";
import card from "../../assets/card.jpg"
import ending from "../../assets/ending.jpg"

function Home() {
  const images = [`${ending}`, `${card}`, `${banner}`];

  const [index, setIndex] = useState(0);
  const [activeTab, setActiveTab] = useState("intro");

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000); // Chuyển ảnh mỗi 3 giây

    return () => clearInterval(interval);
  }, []);


  return (
    <div className="bg-white text-black">
      {/* Banner */}
      <div className="relative w-full h-[400px] bg-cover bg-center" style={{ backgroundImage: `url(${banner})` }}>
        <div className="absolute inset-0 bg-black/50"></div>
      </div>

      {/* Cards */}
      <div className="relative flex justify-center gap-6 py-10 -mt-40">
        {["Bản Lĩnh Hải Vương", "Vượt Thái Bình Dương", "Chinh Phục Đỉnh Cao", "Đương đầu Thử Thách"].map((title, index) => (
          <div key={index} className="relative bg-white shadow-lg rounded-xl overflow-hidden w-52">
            <img src={`${card}`} alt={title} className="w-full h-60 object-cover " />
            <div className="absolute bottom-0 w-full bg-black/50 text-white text-sm font-semibold text-center py-1">
              {title}
            </div>
          </div>
        ))}
      </div>

      {/* Giới thiệu */}
      <div className="flex flex-row items-center justify-center max-w-5xl mx-auto p-6 gap-10">
          {/* Phần chứa Tabs */}
      <div className="max-w-lg text-left w-1/2">
        {/* Thanh Tabs */}
        <div className="relative flex space-x-6 text-lg font-bold border-b border-gray-300">
          {["Giới thiệu", "Thông tin ứng dụng"].map((tab) => (
            <button
              key={tab}
              className={`relative pb-2 transition-all ${
                activeTab === tab.toLowerCase().replace(" ", "-")
                  ? "text-black"
                  : "text-gray-500"
              }`}
              onClick={() => setActiveTab(tab.toLowerCase().replace(" ", "-"))}
            >
              {tab}
              {activeTab === tab.toLowerCase().replace(" ", "-") && (
                <div className="absolute left-0 bottom-0 w-full h-[3px] bg-black transition-all duration-300"></div>
              )}
            </button>
          ))}
        </div>

        {/* Nội dung hiển thị */}
        <div className="mt-4 transition-opacity duration-500">
          {activeTab === "giới-thiệu" && (
            <div key="intro" className="animate-fade-in">
              <h2 className="text-xl font-bold mb-4">GIỚI THIỆU</h2>
              <p className="text-gray-700">
                Hành trình Magellan (HTM) là một cuộc thi kiến thức dành cho học sinh THPT tại Quảng Nam được tổ chức và quản lý bởi học sinh trường THPT chuyên Nguyễn Bỉnh Khiêm.
              </p>
            </div>
          )}
          {activeTab === "thông-tin-ứng-dụng" && (
            <div key="app-info" className="animate-fade-in">
              <h2 className="text-xl font-bold mb-4">THÔNG TIN ỨNG DỤNG</h2>
              <p className="text-gray-700">
                Ứng dụng được xây dựng nhằm hỗ trợ học sinh tham gia thi trực tuyến, theo dõi kết quả và tương tác với cộng đồng học tập.
              </p>
            </div>
          )}
        </div>

        {/* Nút bấm */}
        <div className="flex gap-4 mt-6">
          <button className="px-4 py-2 bg-black text-white rounded-lg">Tham gia phòng thi</button>
          <button className="px-4 py-2 border border-black rounded-lg">Đăng nhập</button>
        </div>
        </div>

        {/* Hình ảnh */}
        <div className="relative w-96 h-64">
          {images.map((img, i) => (
            <img
              key={i}
              src={img}
              alt="Group"
              className={`absolute w-full h-full object-cover rounded-lg shadow-lg transition-opacity duration-1000 ${i === index ? "opacity-100" : "opacity-0"
                }`}
            />
          ))}
        </div>
      </div>
    </div>
  )
}

export default Home
