import Round2 from '../../../layouts/RoundBase/Round2';
import Host from '../../../layouts/Host/Host';
import QuestionBoxRound2 from '../../../layouts/RoundBase/Round2';
import { getQuestionByRound } from '../Test/service';
import React,{useEffect, useState} from 'react';
import { useSearchParams } from 'react-router-dom';


const HostRound2: React.FC = () => {
    const [params] = useSearchParams()
    const roomId = params.get("roomId") || ""
    const testName = params.get("testName") || ""
    const [obstacleWord, setObstacleWord] = useState<string>("")
    const [hintWords, setHintWords] = useState<string[]>([])
    useEffect(()=> {
        const getSecondRoundQuestion = async () => {
            const result = await getQuestionByRound(testName,"2",roomId)
            console.log("round 2 data", result)
            setObstacleWord(result[6].answer)
            const hintWords = []

            for (let i=0; i<result.length-1;i++) {
                hintWords.push(result[i].answer)
            }
            setHintWords(hintWords)
        }

        getSecondRoundQuestion()

    },[])
    return (
        <Host
            QuestionComponent={<QuestionBoxRound2 obstacleWord={obstacleWord} hintWordArray={hintWords} isHost={true}/>}
        />

    )
};

export default HostRound2;
