import Play from "../../layouts/Play";
import React, { useState, useEffect } from "react";

interface HintWord {
    word: string;
    x: number;
    y: number;
    direction: "horizontal" | "vertical";
}

interface ObstacleQuestionBoxProps {
    obstacleWord: string;
}

type PlacedWord = {
    word: string;
    x: number;
    y: number;
    direction: "horizontal" | "vertical";
};

const mainKeyword = "VIETTEL";
const GRID_SIZE = 20; // ðŸ”¹ TÄƒng kÃ­ch thÆ°á»›c lÃªn 20x20

const generateHintWords = (words: string[], gridSize: number): HintWord[] => {
    const placedWords: PlacedWord[] = [];
    const occupiedPositions = new Set<string>();

    const getKey = (x: number, y: number) => `${x},${y}`;

    const canPlaceWord = (word: string, x: number, y: number, direction: "horizontal" | "vertical") => {
        for (let i = 0; i < word.length; i++) {
            let newX = direction === "horizontal" ? x + i : x;
            let newY = direction === "horizontal" ? y : y + i;
            if (newX >= gridSize || newY >= gridSize || occupiedPositions.has(getKey(newX, newY))) {
                return false;
            }
        }
        return true;
    };

    words.forEach((word) => {
        for (let attempt = 0; attempt < 100; attempt++) {
            let direction: "horizontal" | "vertical" = Math.random() < 0.5 ? "horizontal" : "vertical";
            let x = Math.floor(Math.random() * (direction === "horizontal" ? gridSize - word.length : gridSize));
            let y = Math.floor(Math.random() * (direction === "vertical" ? gridSize - word.length : gridSize));

            if (canPlaceWord(word, x, y, direction)) {
                placedWords.push({ word, x, y, direction });
                for (let i = 0; i < word.length; i++) {
                    let newX = direction === "horizontal" ? x + i : x;
                    let newY = direction === "horizontal" ? y : y + i;
                    occupiedPositions.add(getKey(newX, newY));
                }
                break;
            }
        }
    });

    return placedWords;
};

const generateEmptyGrid = () => {
    return Array(GRID_SIZE).fill(null).map(() => Array(GRID_SIZE).fill(""));
};

const ObstacleQuestionBox: React.FC<ObstacleQuestionBoxProps> = ({ obstacleWord }) => {
    const [grid, setGrid] = useState<string[][]>(generateEmptyGrid());
    const [revealedCells, setRevealedCells] = useState<boolean[][]>(
        Array(GRID_SIZE).fill(null).map(() => Array(GRID_SIZE).fill(false))
    );
    const [hintWords, setHintWords] = useState<HintWord[]>([]);

    useEffect(() => {
        const randomHintWords = generateHintWords(["GIÃ“", "Háº T", "DREAM", "THUá»C", "CÃšC", "CHÃ‚U"], GRID_SIZE);
        setHintWords(randomHintWords);
        let newGrid = generateEmptyGrid();

        randomHintWords.forEach(({ word, x, y, direction }) => {
            for (let i = 0; i < word.length; i++) {
                if (direction === "horizontal") {
                    newGrid[y][x + i] = word[i];
                } else {
                    newGrid[y + i][x] = word[i];
                }
            }
        });

        setGrid(newGrid);
    }, []);

    const toggleCell = (row: number, col: number) => {
        setRevealedCells((prev) => {
            const newRevealed = prev.map((r) => [...r]);
            newRevealed[row][col] = !newRevealed[row][col];
            return newRevealed;
        });
    };

    return (
        <div className="flex flex-col items-center bg-white rounded-lg shadow-md p-6">
            <div className="grid grid-cols-20 gap-0">
                {grid.map((row, rowIndex) =>
                    row.map((cell, colIndex) => {
                        const isRevealed = revealedCells[rowIndex][colIndex];

                        return (
                            <div
                                key={`${rowIndex}-${colIndex}`}
                                onClick={() => toggleCell(rowIndex, colIndex)}
                                className={`w-6 h-6 border flex items-center justify-center text-lg font-semibold select-none 
                                    ${isRevealed ? "text-black" : "text-transparent"}
                                    ${obstacleWord.includes(cell) && isRevealed ? "font-bold text-red-500" : ""}
                                    ${cell === "" ? "border-none" : "border-gray-400"}
                                `}
                            >
                                {cell}
                            </div>
                        );
                    })
                )}
            </div>
        </div>
    );
};

function Round2() {
    return <Play questionComponent={<ObstacleQuestionBox obstacleWord={mainKeyword} />} />;
}

export default Round2;
