import './App.css';
import { Routes, Route, useLocation, useSearchParams } from "react-router-dom";
import Home from './pages/Home/Home';
import UserRound1 from './pages/User/UserRound1';
import UserRound2 from './pages/User/Round2/UserRound2';
import UserRound3 from './pages/User/Round3/UserRound3';
import UserRound4 from './pages/User/Round4/UserRound4';

import HostRound1 from './pages/Host/Management/HostRound1';
import HostRound2 from './pages/Host/Management/HostRound2';
import HostRound3 from './pages/Host/Management/HostRound3';
import HostRound4 from './pages/Host/Management/HostRound4';

import CreateRoom from './pages/Host/Room/CreateRoom';

function PlayComponent() {
  const [searchParams] = useSearchParams();
  const round = searchParams.get("round") || "1"; 

  if (round === "1") return <UserRound1 />;
  if (round === "2") return <UserRound2 />;
  if (round === "3") return <UserRound3 />;
  if (round === "4") return <UserRound4 />;

  return <div className="text-center text-red-500">Round không hợp lệ!</div>;
}

function HostComponent() {
  const [searchParams] = useSearchParams();
  const round = searchParams.get("round") || "1"; 

  if (round === "1") return <HostRound1 />;
  if (round === "2") return <HostRound2 />;
  if (round === "3") return <HostRound3 />;
  if (round === "4") return <HostRound4 />;

  return <div className="text-center text-red-500">Round không hợp lệ!</div>;
}

function App() {
  const [searchParams] = useSearchParams();
  const roundName = `Round${searchParams.get("round") || "1"}`
  return (
    <>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/play" element={<PlayComponent />} />
        <Route path="/host" element={<CreateRoom />} />
        <Route path="/host" element={<HostComponent />} />
      </Routes>
    </>
  );
}

export default App;
