import Play from "../../layouts/Play";
import React, { useState, useEffect } from "react";

interface HintWord {
    word: string;
    x: number;
    y: number;
    direction: "horizontal" | "vertical";
}

interface ObstacleQuestionBoxProps {
    obstacleWord: string;
}

type PlacedWord = {
    word: string;
    x: number;
    y: number;
    direction: "horizontal" | "vertical";
};

// Tá»« khÃ³a chÆ°á»›ng ngáº¡i váº­t
const mainKeyword = "VIETTEL";

// Táº¡o tá»« khÃ³a gá»£i Ã½ vá»›i vá»‹ trÃ­ vÃ  hÆ°á»›ng random
const generateHintWords = (words: string[], gridSize: number, obstacleWord: string): HintWord[] => {
    const grid: string[][] = Array(gridSize)
        .fill(null)
        .map(() => Array(gridSize).fill("")); // Máº£ng kiá»ƒm tra vá»‹ trÃ­

    const findCommonLetterPairs = (words: string[]): { word1: string; word2: string; commonLetters: string[] }[] => {
        let results: { word1: string; word2: string; commonLetters: string[] }[] = [];
        let usedWords = new Set<string>(); // LÆ°u láº¡i cÃ¡c tá»« Ä‘Ã£ dÃ¹ng

        let remainingPairs: { word1: string; word2: string; commonLetters: string[] }[] = [];

        for (let i = 0; i < words.length; i++) {
            for (let j = i + 1; j < words.length; j++) {
                const word1 = words[i];
                const word2 = words[j];

                // TÃ¬m kÃ½ tá»± chung giá»¯a 2 tá»«
                const commonLetters = [...new Set(word1)].filter((letter) => word2.includes(letter));

                if (commonLetters.length >= 2) {
                    // TÃ­nh Ä‘iá»ƒm Æ°u tiÃªn (chá»¯ gáº§n trung tÃ¢m hÆ¡n)
                    const scoredLetters = commonLetters.map((letter) => {
                        const index1 = word1.indexOf(letter);
                        const index2 = word2.indexOf(letter);
                        const center1 = word1.length / 2;
                        const center2 = word2.length / 2;
                        const score = Math.abs(index1 - center1) + Math.abs(index2 - center2);
                        return { letter, score };
                    });

                    // Sáº¯p xáº¿p theo Ä‘iá»ƒm Æ°u tiÃªn
                    scoredLetters.sort((a, b) => a.score - b.score);
                    const prioritizedLetters = scoredLetters.map((item) => item.letter);

                    remainingPairs.push({ word1, word2, commonLetters: prioritizedLetters });
                }
            }
        }

        // BÆ°á»›c 2: Chá»n tá»‘i Ä‘a 2 cáº·p theo quy táº¯c
        for (const pair of remainingPairs) {
            if (results.length >= 2) break; // Äá»§ 2 cáº·p thÃ¬ dá»«ng
            if (!usedWords.has(pair.word1) && !usedWords.has(pair.word2)) {
                results.push(pair);
                usedWords.add(pair.word1);
                usedWords.add(pair.word2);
            }
        }

        // Náº¿u váº«n chÆ°a Ä‘á»§ 2 cáº·p, chá»n tiáº¿p tá»« cÃ¡c cáº·p cÃ³ tá»« Ä‘Ã£ dÃ¹ng
        for (const pair of remainingPairs) {
            if (results.length >= 2) break;
            if (!usedWords.has(pair.word1) || !usedWords.has(pair.word2)) {
                results.push(pair);
                usedWords.add(pair.word1);
                usedWords.add(pair.word2);
            }
        }

        return results;
    };

    // ðŸ›  Test
    const testwords = ["bÆ°uchÃ­nh", "5G", "bqp", "táº­pÄ‘oÃ n", "hÃ ná»™i", "red"];

    const shuffledWords = testwords.sort(() => Math.random() - 0.5);
    
    const placedWords = placeWordsOnGrid(shuffledWords, gridSize);
    console.log(placedWords);

    return placedWords;
};
const ObstacleQuestionBox: React.FC<ObstacleQuestionBoxProps> = ({ obstacleWord }) => {

    // Increased grid size
    const GRID_SIZE = 15;

    const generateEmptyGrid = () => {
        return Array(GRID_SIZE).fill(null).map(() => Array(GRID_SIZE).fill(""));
    };
    const [grid, setGrid] = useState<string[][]>(generateEmptyGrid());
    const [revealedRows, setRevealedRows] = useState<boolean[]>(Array(GRID_SIZE).fill(false));
    const [revealedCols, setRevealedCols] = useState<boolean[]>(Array(GRID_SIZE).fill(false));
    const [hintWords, setHintWords] = useState<HintWord[]>([]);



    useEffect(() => {
        const wordList = ["bÆ°uchÃ­nh", "5G", "bqp", "táº­pÄ‘oÃ n", "hÃ ná»™i", "red"];
        let randomHintWords: HintWord[] = [];
        let newGrid = generateEmptyGrid();
        let occupiedPositions = new Set<string>();
        let occupiedRows = new Set<number>();
        let occupiedCols = new Set<number>();

        const getKey = (x: number, y: number) => `${x},${y}`;

        const canPlaceNumber = (x: number, y: number) => {
            if (x < 0 || y < 0 || x >= GRID_SIZE || y >= GRID_SIZE) return false;
            return !occupiedPositions.has(getKey(x, y));
        };

        const hasCommonLetter = (word1: string, word2: string) => {
            return [...new Set(word1)].some((letter) => word2.includes(letter));
        };

        // Try to place words and numbers until a valid configuration is found
        let attempts = 0;
        const maxAttempts = 100;

        while (attempts < maxAttempts) {
            randomHintWords = generateHintWords(wordList, GRID_SIZE, mainKeyword);
            newGrid = generateEmptyGrid();
            occupiedPositions.clear();
            occupiedRows.clear();
            occupiedCols.clear();

            let canPlaceAll = true;

            // First pass: Place words and mark their positions
            for (const { word, x, y, direction } of randomHintWords) {
                if (direction === "horizontal") {
                    occupiedRows.add(y);
                    // Check for adjacent rows
                    for (const other of randomHintWords) {
                        if (other.word === word || other.direction !== "horizontal") continue; // Fixed comparison
                        if (Math.abs(other.y - y) === 1 && !hasCommonLetter(word, other.word)) {
                            canPlaceAll = false;
                            break;
                        }
                    }
                } else {
                    occupiedCols.add(x);
                    // Check for adjacent columns
                    for (const other of randomHintWords) {
                        if (other.word === word || other.direction !== "vertical") continue; // Fixed comparison
                        if (Math.abs(other.x - x) === 1 && !hasCommonLetter(word, other.word)) {
                            canPlaceAll = false;
                            break;
                        }
                    }
                }

                if (!canPlaceAll) break;

                for (let i = 0; i < word.length; i++) {
                    let newX = direction === "horizontal" ? x + i : x;
                    let newY = direction === "horizontal" ? y : y + i;
                    newGrid[newY][newX] = word[i];
                    occupiedPositions.add(getKey(newX, newY));
                }
            }

            if (!canPlaceAll) {
                attempts++;
                continue;
            }

            // Second pass: Check if numbers can be placed without overlap
            for (const { word, x, y, direction } of randomHintWords) {
                const wordNumber = wordList.indexOf(word) + 1;

                if (direction === "horizontal" && x > 0) {
                    if (!canPlaceNumber(x - 1, y)) {
                        canPlaceAll = false;
                        break;
                    }
                } else if (direction === "vertical" && y > 0) {
                    if (!canPlaceNumber(x, y - 1)) {
                        canPlaceAll = false;
                        break;
                    }
                }
            }

            if (canPlaceAll) {
                // Place numbers if all checks pass
                randomHintWords.forEach(({ word, x, y, direction }) => {
                    const wordNumber = wordList.indexOf(word) + 1;
                    if (direction === "horizontal" && x > 0) {
                        newGrid[y][x - 1] = wordNumber.toString();
                        occupiedPositions.add(getKey(x - 1, y));
                    } else if (direction === "vertical" && y > 0) {
                        newGrid[y - 1][x] = wordNumber.toString();
                        occupiedPositions.add(getKey(x, y - 1));
                    }
                });
                break; // Valid configuration found, exit loop
            }

            attempts++;
        }

        if (attempts >= maxAttempts) {
            console.warn("Could not find a valid grid configuration without overlapping numbers or adjacent non-shared words after max attempts.");
        }

        setHintWords(randomHintWords);
        setGrid(newGrid);
    }, []);

    // HÃ m toggle hiá»ƒn thá»‹ hÃ ng/cá»™t
    const toggleRow = (rowIndex: number) => {
        setRevealedRows((prev) => {
            const newRevealed = [...prev];
            newRevealed[rowIndex] = !newRevealed[rowIndex];
            return newRevealed;
        });
    };

    const toggleCol = (colIndex: number) => {
        setRevealedCols((prev) => {
            const newRevealed = [...prev];
            newRevealed[colIndex] = !newRevealed[colIndex];
            return newRevealed;
        });
    };

    return (
        <div className="flex flex-col items-center bg-white rounded-lg shadow-md p-6">
            <div className="grid grid-cols-[40px_repeat(15,40px)] gap-1">
                {/* Empty header for alignment */}
                <div className="w-10 h-10 bg-white" />
                {Array.from({ length: GRID_SIZE }).map((_, colIndex) => (
                    <div key={colIndex} className="w-10 h-10 bg-white" />
                ))}

                {/* Grid with numbers and cells */}
                {grid.map((row, rowIndex) => (
                    <React.Fragment key={rowIndex}>
                        <div className="w-10 h-10 bg-white" /> {/* Empty row label for alignment */}
                        {row.map((cell, colIndex) => {
                            const isRevealed =
                                (revealedRows[rowIndex] &&
                                    hintWords.some(
                                        (word) => word.y === rowIndex && word.direction === "horizontal"
                                    )) ||
                                (revealedCols[colIndex] &&
                                    hintWords.some((word) => word.x === colIndex && word.direction === "vertical"));

                            return (
                                <div
                                    key={colIndex}
                                    className={`w-10 h-10 flex items-center justify-center text-lg font-semibold select-none 
                                    
                                    ${!isNaN(Number(cell)) ? "blue-500 font-bold " : ""}
                                    ${cell === "" ? "bg-white border-none" : "border-gray-400 bg-gray-50"} 
                                    ${isRevealed && isNaN(Number(cell)) ? "text-black" : isNaN(Number(cell)) ? "text-transparent" : "text-blue-500"}
                                    ${obstacleWord.includes(cell) && isRevealed && isNaN(Number(cell)) ? "font-bold text-red-500" : ""}
                                `}
                                    onClick={() => {
                                        if (hintWords.some((word) => word.y === rowIndex && word.direction === "horizontal")) {
                                            toggleRow(rowIndex);
                                        } else if (hintWords.some((word) => word.x === colIndex && word.direction === "vertical")) {
                                            toggleCol(colIndex);
                                        }
                                    }}
                                    style={{
                                        cursor: hintWords.some((word) => word.y === rowIndex || word.x === colIndex)
                                            ? "pointer"
                                            : "default",
                                    }}
                                >
                                    {cell}
                                </div>
                            );
                        })}
                    </React.Fragment>
                ))}
            </div>
        </div>
    );
};
function Round2() {
    return <Play questionComponent={<ObstacleQuestionBox obstacleWord={mainKeyword} />} />;
}

export default Round2;
