import Play from "../../layouts/Play";
import React, { useState, useEffect } from "react";


interface HintWord {
    word: string;
    x: number;
    y: number;
    direction: "horizontal" | "vertical";
}

interface ObstacleQuestionBoxProps {
    obstacleWord: string;
}

type PlacedWord = {
    word: string;
    x: number;
    y: number;
    direction: "horizontal" | "vertical";
};

// T·ª´ kh√≥a ch∆∞·ªõng ng·∫°i v·∫≠t
const mainKeyword = "BOCONGANH";

// T·∫°o t·ª´ kh√≥a g·ª£i √Ω v·ªõi v·ªã tr√≠ v√† h∆∞·ªõng random
const generateHintWords = (words: string[], gridSize: number, obstacleWord: string): HintWord[] => {

    const grid: string[][] = Array(gridSize).fill(null).map(() => Array(gridSize).fill("")); // M·∫£ng ki·ªÉm tra v·ªã tr√≠

    const findCommonLetterPairs = (words: string[]): { word1: string; word2: string; commonLetters: string[] }[] => {
        let results: { word1: string; word2: string; commonLetters: string[] }[] = [];
        let usedWords = new Set<string>(); // L∆∞u l·∫°i c√°c t·ª´ ƒë√£ d√πng

        let remainingPairs: { word1: string; word2: string; commonLetters: string[] }[] = [];

        for (let i = 0; i < words.length; i++) {
            for (let j = i + 1; j < words.length; j++) {
                const word1 = words[i];
                const word2 = words[j];

                // T√¨m k√Ω t·ª± chung gi·ªØa 2 t·ª´
                const commonLetters = [...new Set(word1)].filter(letter => word2.includes(letter));

                if (commonLetters.length >= 2) {
                    // T√≠nh ƒëi·ªÉm ∆∞u ti√™n (ch·ªØ g·∫ßn trung t√¢m h∆°n)
                    const scoredLetters = commonLetters.map(letter => {
                        const index1 = word1.indexOf(letter);
                        const index2 = word2.indexOf(letter);
                        const center1 = word1.length / 2;
                        const center2 = word2.length / 2;
                        const score = Math.abs(index1 - center1) + Math.abs(index2 - center2);
                        return { letter, score };
                    });

                    // S·∫Øp x·∫øp theo ƒëi·ªÉm ∆∞u ti√™n
                    scoredLetters.sort((a, b) => a.score - b.score);
                    const prioritizedLetters = scoredLetters.map(item => item.letter);

                    remainingPairs.push({ word1, word2, commonLetters: prioritizedLetters });
                }
            }
        }

        // B∆∞·ªõc 2: Ch·ªçn t·ªëi ƒëa 2 c·∫∑p theo quy t·∫Øc
        for (const pair of remainingPairs) {
            if (results.length >= 2) break; // ƒê·ªß 2 c·∫∑p th√¨ d·ª´ng
            if (!usedWords.has(pair.word1) && !usedWords.has(pair.word2)) {
                results.push(pair);
                usedWords.add(pair.word1);
                usedWords.add(pair.word2);
            }
        }

        // N·∫øu v·∫´n ch∆∞a ƒë·ªß 2 c·∫∑p, ch·ªçn ti·∫øp t·ª´ c√°c c·∫∑p c√≥ t·ª´ ƒë√£ d√πng
        for (const pair of remainingPairs) {
            if (results.length >= 2) break;
            if (!usedWords.has(pair.word1) || !usedWords.has(pair.word2)) {
                results.push(pair);
                usedWords.add(pair.word1);
                usedWords.add(pair.word2);
            }
        }

        return results;
    };

    // üõ† Test
    const testwords = ["gi√≥", "h·∫°tgi·ªëng", "c√∫c", "thu·ªëc", "dream", "ch√¢uki·ªátlu"];


    const placeWordsOnGrid = (words: string[], gridSize: number): PlacedWord[] => {
        let placedWords: PlacedWord[] = [];
        let occupiedPositions = new Set<string>();
        let placedSet = new Set<string>(); // Set ƒë·ªÉ theo d√µi t·ª´ ƒë√£ ƒë·∫∑t

        const getKey = (x: number, y: number) => `${x},${y}`;

        const canPlaceWord = (word: string, x: number, y: number, direction: "horizontal" | "vertical", index: number) => {
            for (let i = 0; i < word.length; i++) {
                if(i == x +index ) continue
                let newX = direction === "horizontal" ? x + i : x ;
                let newY = direction === "horizontal" ? y  : y + i ;
                if (newX < 0 || newY < 0 || newX >= gridSize || newY >= gridSize || occupiedPositions.has(getKey(newX, newY))) {
                    return false;
                }
            }
            return true;
        };

        const commonLetterPairs = findCommonLetterPairs(words);
        console.log(commonLetterPairs)

        for (const { word1, word2, commonLetters } of commonLetterPairs) {
            if (placedSet.has(word1) || placedSet.has(word2)) continue; // Ki·ªÉm tra n·∫øu ƒë√£ ƒë·∫∑t th√¨ b·ªè qua

            const commonLetter = commonLetters[0];
            const index1 = word1.indexOf(commonLetter);
            const index2 = word2.indexOf(commonLetter);

            let placed = false;
            for (let attempt = 0; attempt < 100; attempt++) {
                let x = Math.floor(Math.random() * (gridSize - word1.length));
                let y = Math.floor(Math.random() * (gridSize - index2) + index2);

                if (canPlaceWord(word1, x, y, "horizontal", index1)) {
                    placedWords.push({ word: word1, x, y, direction: "horizontal" });
                    placedSet.add(word1); // ƒê√°nh d·∫•u t·ª´ ƒë√£ ƒë·∫∑t

                    

                    let intersecrX = x + index1;
                    let intersectY = y ;
                    
                    for (let i = 0; i < word1.length; i++) occupiedPositions.add(getKey(x+i, y));

                    if (canPlaceWord(word2, intersecrX, intersectY -index2, "vertical", index2)) {
                        placedWords.push({ word: word2, x: intersecrX, y: intersectY -index2, direction: "vertical" });
                        placedSet.add(word2);

                        for (let i = 0; i < word2.length; i++) occupiedPositions.add(getKey(intersecrX - index2+i, intersectY));

                        placed = true;
                        break;
                    }
                } else {
                    
                }
            }

            if (!placed) console.warn(`Kh√¥ng th·ªÉ ƒë·∫∑t c·∫∑p t·ª´: ${word1} - ${word2}`);
        }

        return placedWords;
    };
    const placedWords = placeWordsOnGrid(testwords, gridSize);
    console.log(placedWords);

    return placedWords;
};

// K√≠ch th∆∞·ªõc l∆∞·ªõi
const GRID_SIZE = 10;

// T·∫°o l∆∞·ªõi tr·ªëng
const generateEmptyGrid = () => {
    return Array(GRID_SIZE).fill(null).map(() => Array(GRID_SIZE).fill(""));
};


const ObstacleQuestionBox: React.FC<ObstacleQuestionBoxProps> = ({ obstacleWord }) => {
    const [grid, setGrid] = useState<string[][]>(generateEmptyGrid());
    const [revealedRows, setRevealedRows] = useState<boolean[]>(Array(GRID_SIZE).fill(false));
    const [revealedCols, setRevealedCols] = useState<boolean[]>(Array(GRID_SIZE).fill(false));
    const [hintWords, setHintWords] = useState<HintWord[]>([]);


    useEffect(() => {

        const randomHintWords = generateHintWords(["GI√ì", "H·∫†T", "DREAM", "THU·ªêC", "C√öC", "CH√ÇU"], GRID_SIZE, mainKeyword);
        setHintWords(randomHintWords);
        let newGrid = generateEmptyGrid();

        // Th√™m t·ª´ v√†o l∆∞·ªõi
        randomHintWords.forEach(({ word, x, y, direction }) => {
            for (let i = 0; i < word.length; i++) {
                if (direction === "horizontal") {
                    newGrid[y][x + i] = word[i];
                } else {
                    newGrid[y + i][x] = word[i];
                }
            }
        });

        setGrid(newGrid);
    }, []);

    // H√†m toggle hi·ªÉn th·ªã h√†ng/c·ªôt
    const toggleRow = (rowIndex: number) => {
        setRevealedRows((prev) => {
            const newRevealed = [...prev];
            newRevealed[rowIndex] = !newRevealed[rowIndex];
            return newRevealed;
        });
    };

    const toggleCol = (colIndex: number) => {
        setRevealedCols((prev) => {
            const newRevealed = [...prev];
            newRevealed[colIndex] = !newRevealed[colIndex];
            return newRevealed;
        });
    };

    return (
        <div className="flex flex-col items-center bg-white rounded-lg shadow-md p-6">
            <div className="grid grid-cols-[30px_repeat(10,40px)] gap-1">
                {/* H√†ng ƒë·∫ßu ti√™n ch·ª©a s·ªë c·ªôt */}
                <div></div> {/* √î tr·ªëng ·ªü g√≥c */}
                {Array.from({ length: GRID_SIZE }).map((_, colIndex) => (
                    <button
                        key={colIndex}
                        className="font-bold text-blue-500 cursor-pointer"
                        onClick={() => toggleCol(colIndex)}
                    >
                        {colIndex + 1}
                    </button>
                ))}

                {/* Hi·ªÉn th·ªã l∆∞·ªõi ch·ªØ */}
                {grid.map((row, rowIndex) => (
                    <React.Fragment key={rowIndex}>
                        {/* S·ªë h√†ng b√™n tr√°i */}
                        <button
                            className="font-bold text-blue-500 cursor-pointer"
                            onClick={() => toggleRow(rowIndex)}
                        >
                            {rowIndex + 1}
                        </button>

                        {row.map((cell, colIndex) => {
                            const isRevealed =
                                (revealedRows[rowIndex] && hintWords.some((word) => word.y === rowIndex && word.direction === "horizontal")) ||
                                (revealedCols[colIndex] && hintWords.some((word) => word.x === colIndex && word.direction === "vertical"));

                            return (
                                <div
                                    key={colIndex}
                                    className={`w-10 h-10 border flex items-center justify-center text-lg font-semibold select-none 
                                    ${isRevealed ? "text-black" : "text-transparent"}
                                    ${obstacleWord.includes(cell) && isRevealed ? "font-bold text-red-500" : ""}
                                    ${cell === "" ? "border-none" : "border-gray-400"}
                                `}
                                >
                                    {cell}
                                </div>
                            );
                        })}
                    </React.Fragment>
                ))}
            </div>
        </div>
    );
};

function Round2() {
    return <Play questionComponent={<ObstacleQuestionBox obstacleWord={mainKeyword} />} />;
}

export default Round2;
