export const getTest = async (testId: string): Promise<any> => {
    try {
      // Gửi yêu cầu đến API
      const response = await fetch(`http://127.0.0.1:8000/api/test/get_test/${testId}`, {
        method: 'GET', // Phương thức HTTP
        headers: {
          'Content-Type': 'application/json',
        //   Authorization: `Bearer YOUR_API_TOKEN`, // Nếu API yêu cầu token
        },
      });
  
      // Kiểm tra nếu response không thành công
      if (!response.ok) {
        throw new Error(`Failed to fetch test with ID: ${testId}, Status: ${response.status}`);
      }
  
      // Phân tích dữ liệu từ response JSON
      const data = await response.json();
      return data.questions;
  
    } catch (error) {
      console.error('Error fetching test data:', error);
      throw error; // Quăng lỗi để xử lý ở nơi gọi hàm
    }
  };

  export const uploadTestToServer = async (file: File): Promise<{ message: string; data?: any }> => {
    // Kiểm tra nếu không có file
    if (!file) {
      throw new Error("Không có file nào được chọn!");
    }
  
    // Tạo đối tượng FormData để đóng gói file
    const formData = new FormData();
    formData.append("file", file);
  
    try {
      // Gửi yêu cầu POST tới server
      const response = await fetch("http://127.0.0.1:8000/api/test/upload", {
        method: "POST",
        body: formData,
      });
  
      // Kiểm tra phản hồi HTTP
      if (!response.ok) {
        throw new Error(`Upload thất bại: ${response.statusText}`);
      }
  
      // Giải mã JSON từ server
      const result = await response.json();
      console.log("Upload thành công:", result);
      return { message: "Upload thành công!", data: result };
    } catch (error: any) {
      console.error("Lỗi khi upload file:", error.message);
      throw new Error(`Lỗi upload: ${error.message}`);
    }
  };
