import { useEffect, useState } from "react";
import User from "../../../layouts/User/User";
import Round2 from "../../../layouts/RoundBase/Round2";
import ReactPlaceholder from "react-placeholder";
import "react-placeholder/lib/reactPlaceholder.css";
import { listenToGrid, listenToRoundStart } from "../../../services/firebaseServices";
import { usePlayer } from "../../../context/playerContext";
function UserRound2() {

    const [loading, setLoading] = useState(true);
    const [data, setData] = useState<string>("");
    const {roomId, initialGrid} = usePlayer()
    //const [grid, setGrid] = useState<string[][]>([])
    useEffect(() => {

        if (initialGrid && initialGrid.length > 0) {
            setLoading(false); 
        }
    }, [initialGrid]);

    // useEffect(() => {
    //         const unsubscribePlayers = listenToGrid(roomId, (grid) => {
    //             setGrid(grid)
    //         });

    //         return () => {
    //             unsubscribePlayers();
    //         };
    //     }, []);

    return (
        <ReactPlaceholder
            type="text"
            rows={3}
            ready={!loading}
        >

            <User
                QuestionComponent={<Round2 initialGrid={initialGrid} isHost={false}/>}
            />
        </ReactPlaceholder>
    )

}

export default UserRound2;
