import React from 'react';
import Play from '../../../layouts/Play'; // Importing the shared Play component

const HostRound4: React.FC = () => {
    const playerCount = 4; // Assuming 4 players for score management
    const [playerScores, setPlayerScores] = React.useState<number[]>(Array(playerCount).fill(0)); // Initial scores
    const [correctAnswer, setCorrectAnswer] = React.useState<string | null>(null);
    const [isTimerActive, setIsTimerActive] = React.useState(false);

    // Handle score adjustment for a specific player
    const handleScoreAdjust = (playerIndex: number, amount: number) => {
        setPlayerScores((prevScores) => {
            const newScores = [...prevScores];
            newScores[playerIndex] += amount;
            return newScores;
        });
    };

    // Display the correct answer
    const handleShowAnswer = () => {
        setCorrectAnswer('This is the correct answer!');
    };

    // Start the timer
    const handleStartTimer = () => {
        setIsTimerActive(true);
    };

    return (
        <div className="w-screen h-screen flex flex-col">
            {/* Using the shared Play component */}
            <Play questionComponent={<div>Host Question Component</div>} />

            {/* Host-specific controls */}
            <div className="fixed bottom-0 left-0 w-full bg-white p-4 shadow-lg">
                <h3 className="text-xl font-bold text-black mb-4">Host Controls</h3>

                <div className="flex flex-col gap-4">
                    {/* Score adjustment controls */}
                    <div className="flex gap-4">
                        {playerScores.map((score, index) => (
                            <div key={index} className="flex flex-col items-center">
                                <p className="text-black font-bold">Player {index + 1}</p>
                                <p className="text-black mb-2">Score: {score}</p>
                                <div className="flex gap-2">
                                    {[10, 20, 30].map((amount) => (
                                        <button
                                            key={`player-${index}-add-${amount}`}
                                            onClick={() => handleScoreAdjust(index, amount)}
                                            className="bg-green-500 text-white p-2 rounded-md"
                                        >
                                            +{amount}
                                        </button>
                                    ))}
                                    {[10, 20, 30].map((amount) => (
                                        <button
                                            key={`player-${index}-subtract-${amount}`}
                                            onClick={() => handleScoreAdjust(index, -amount)}
                                            className="bg-red-500 text-white p-2 rounded-md"
                                        >
                                            -{amount}
                                        </button>
                                    ))}
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Show answer */}
                    <button
                        onClick={handleShowAnswer}
                        className="bg-blue-500 text-white p-3 rounded-md"
                    >
                        Show Answer
                    </button>

                    {/* Timer controls */}
                    <button
                        onClick={handleStartTimer}
                        className="bg-purple-500 text-white p-3 rounded-md"
                    >
                        Start Timer
                    </button>

                    {/* Correct/Incorrect buttons */}
                    <div className="flex gap-4">
                        <button
                            onClick={() => alert('Correct Answer!')}
                            className="bg-green-500 text-white p-3 rounded-md flex-1"
                        >
                            Correct
                        </button>
                        <button
                            onClick={() => alert('Incorrect Answer!')}
                            className="bg-red-500 text-white p-3 rounded-md flex-1"
                        >
                            Incorrect
                        </button>
                    </div>
                </div>

                {/* Display correct answer */}
                {correctAnswer && (
                    <p className="mt-4 text-black font-bold">Correct Answer: {correctAnswer}</p>
                )}
            </div>
        </div>
    );
};

export default HostRound4;
