import React, { useState, useEffect } from 'react';
import banner from "../../assets/banner.jpg";
import card from "../../assets/card.jpg";
import ending from "../../assets/ending.jpg";

const Login = () => {
  const images = [banner, card, ending];

  const messages = [
    "Ch√†o m·ª´ng ƒë·∫øn v·ªõi H√†nh Tr√¨nh Magellan",
    "Kh√°m ph√° th·∫ø gi·ªõi c√πng Magellan",
    "H√†nh tr√¨nh b·∫Øt ƒë·∫ßu t·ª´ ƒë√¢y",
  ];

  // State ƒë·ªÉ qu·∫£n l√Ω index c·ªßa ·∫£nh v√† text hi·ªán t·∫°i
  const [currentIndex, setCurrentIndex] = useState(0);

  // Chuy·ªÉn ƒë·ªïi ·∫£nh v√† text m·ªói 6 gi√¢y
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % messages.length);
    }, 6000); // 6 gi√¢y
    return () => clearInterval(interval);
  }, [messages.length]);

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-b from-gray-100 to-gray-300">
      <div className="w-full max-w-3xl bg-white rounded-xl shadow-lg flex border-2 border-gray-300">
        {/* Ph·∫ßn ch√†o m·ª´ng v·ªõi animation */}
        <div className="w-1/2 relative overflow-hidden rounded-r-[4rem]">
          {/* Ph·∫ßn ·∫£nh n·ªÅn lu√¢n phi√™n */}
          <div
            className="absolute inset-0 bg-cover opacity-0 animate-imageFade"
            style={{
              backgroundImage: `url(${images[currentIndex]})`,
            }}
            key={currentIndex} // Thay ƒë·ªïi key ƒë·ªÉ k√≠ch ho·∫°t animation khi index thay ƒë·ªïi
          />
          {/* Ph·∫ßn ch·ªØ ch·∫°y v√† m·ªù d·∫ßn */}
          <div className="relative z-10 flex flex-col items-center justify-center h-full p-8 text-white">
            <h2
              className="text-3xl font-bold mb-4 opacity-0 animate-textFade"
              key={currentIndex} // Thay ƒë·ªïi key ƒë·ªÉ ch·ªâ hi·ªÉn th·ªã m·ªôt text t·∫°i m·ªôt th·ªùi ƒëi·ªÉm
            >
              {messages[currentIndex]}
            </h2>
          </div>
        </div>

        {/* Ph·∫ßn form ƒëƒÉng nh·∫≠p */}
        <div className="w-1/2 p-8">
          <h2 className="text-3xl font-bold text-center mb-8">Login</h2>
          <form>
            <div className="mb-6 relative">
              <label className="block text-gray-700 text-lg font-bold mb-2" htmlFor="username">
                Username
              </label>
              <input
                className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-600 text-lg bg-gray-200"
                type="text"
                id="username"
                placeholder="Enter your username"
              />
              <span className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-500">üë§</span>
            </div>
            <div className="mb-6 relative">
              <label className="block text-gray-700 text-lg font-bold mb-2" htmlFor="password">
                Password
              </label>
              <input
                className="w-full px-4 py-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-600 text-lg bg-gray-200"
                type="password"
                id="password"
                placeholder="Enter your password"
              />
              <span className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-500">üîí</span>
            </div>
            <div className="mb-6">
              <a href="#" className="block text-lg text-blue-600 hover:underline mb-4">Forgot Password?</a>
              <button
                type="submit"
                className="w-full px-4 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 text-lg"
              >
                Login
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
