import Play from "../../layouts/Play";
import React, { useState, useEffect } from "react";
import clsx from "clsx";

// Từ khóa chướng ngại vật
const mainKeyword = "BOCONGANH";
const hintWords: { word: string; x: number; y: number; direction: "horizontal" | "vertical" }[] = [
    { word: "GIÓ", x: 1, y: 2, direction: "vertical" },
    { word: "HẠT", x: 2, y: 2, direction: "vertical" },
    { word: "DREAM", x: 3, y: 1, direction: "horizontal" },
    { word: "THUỐC", x: 4, y: 4, direction: "horizontal" },
    { word: "CÚC", x: 6, y: 5, direction: "vertical" },
    { word: "CHÂU", x: 6, y: 6, direction: "vertical" },
];
interface CrosswordCell {
    letter: string;
    revealed: boolean;
    highlight: boolean;
}

interface CrosswordData {
    [position: string]: CrosswordCell;
}

interface ObstacleQuestionBoxProps {
    mainKeyword: string;
    hintWords: { word: string; x: number; y: number; direction: "horizontal" | "vertical" }[];
}

const ObstacleQuestionBox: React.FC<ObstacleQuestionBoxProps> = ({ mainKeyword, hintWords }) => {
    const [grid, setGrid] = useState<CrosswordData>({});

    useEffect(() => {
        const newGrid: CrosswordData = {};
        const sharedLetters = new Set<string>(); // Các chữ cái chung giữa gợi ý & từ chính

        // Đặt các từ gợi ý lên lưới
        hintWords.forEach(({ word, x, y, direction }) => {
            for (let i = 0; i < word.length; i++) {
                const posX = direction === "horizontal" ? x + i : x;
                const posY = direction === "horizontal" ? y : y + i;
                const position = `${posX}-${posY}`;

                // Kiểm tra nếu chữ cái này cũng có trong từ chướng ngại vật
                const isShared = mainKeyword.includes(word[i]);
                if (isShared) {
                    sharedLetters.add(word[i]);
                }

                newGrid[position] = {
                    letter: word[i],
                    revealed: false,
                    highlight: isShared,
                };
            }
        });

        setGrid(newGrid);
    }, [mainKeyword, hintWords]);

    return (
        <div className="flex flex-col items-center p-4">
            <h2 className="text-xl font-bold mb-4">Chướng Ngại Vật</h2>
            <div className="grid grid-cols-10 gap-1 border border-gray-400 p-2">
                {[...Array(10)].map((_, row) =>
                    [...Array(10)].map((_, col) => {
                        const key = `${col}-${row}`;
                        const cell = grid[key];

                        return cell ? (
                            <button
                                key={key}
                                className={clsx(
                                    "w-8 h-8 flex items-center justify-center border border-gray-500 bg-gray-200",
                                    {
                                        "text-black": cell.revealed,
                                        "text-red-600 font-bold": cell.revealed && cell.highlight,
                                    }
                                )}
                                onClick={() =>
                                    setGrid((prev) => ({
                                        ...prev,
                                        [key]: { ...prev[key], revealed: true },
                                    }))
                                }
                            >
                                {cell.revealed ? cell.letter : ""}
                            </button>
                        ) : (
                            <div key={key} className="w-8 h-8"></div>
                        );
                    })
                )}
            </div>
        </div>
    );
};


function Round2() {
    return <Play questionComponent={<ObstacleQuestionBox mainKeyword={mainKeyword} hintWords={hintWords} />} />;

}

export default Round2
