import React, { useState, useEffect } from "react";

// Define word object structure

type WordPlacement = {
  x: number;
  y: number;
  dir: 0 | 1;
};

interface CrosswordGridProps {
  placedWords?: WordPlacement[];
  boardSize?: number;
}


class WordObj {
  WordObj(stringValue){
      this.string = stringValue;
      this.char = stringValue.split("");
      this.totalMatches = 0;
      this.effectiveMatches = 0;
      this.successfulMatches = [];
    }

}
const CrosswordGrid: React.FC<CrosswordGridProps> = ({ placedWords, boardSize = 20 }) => {

  type Direction = 0 | 1; // 0: horizontal, 1: vertical

interface Match {
  wordIndex: number;
  indexInWord: number;
}

interface WordPlacement {
  word: string;
  x: number;
  y: number;
  dir: Direction;
  successfulMatches: Match[];
  totalMatches: number;
  effectiveMatches: number;
  char: string[];
  string: string;
}

const GRID_SIZE = 18;
const EMPTY_CELL = ' ';

function createEmptyGrid(size: number): string[][] {
  return Array.from({ length: size }, () => Array(size).fill(EMPTY_CELL));
}

  const board = createEmptyGrid(GRID_SIZE)
  let wordBank: string[] = []
  const wordArr = [
    'GIÓ',
    'HẠTGIỐNG',
    'DREAM',
    'THUỐC',
    'CÚC',
    'CHÂUKIỆTLUÂN',
  ]
  function PopulateBoard(){
      PrepareBoard();

      for(var i=0,isOk=true,len=wordBank.length; i<len && isOk; i++){
        isOk = AddWordToBoard();
      }
      return isOk;
    }


    function PrepareBoard(){
      wordBank=[];

      for(var i = 0, len = wordArr.length; i < len; i++){
        let actualPos = i+1;
        wordBank.push(new WordObj(' '+actualPos.toString()+wordArr[i]+' '));
      }

      for(i = 0; i < wordBank.length; i++){
        for(var j = 0, wA=wordBank[i]; j<wA.char.length; j++){
          for(var k = 0, cA=wA.char[j]; k<wordBank.length; k++){
            for(var l = 0,wB=wordBank[k]; k!==i && l<wB.char.length; l++){
              wA.totalMatches += (cA === wB.char[l])?1:0;
            }
          }
        }
      }
    }

    function findWordIdx(givenWord){
      givenWord = givenWord.substring(2, givenWord.length-1);
      for(var i = 0, len = wordArr.length; i < len; i++){
        if(givenWord == wordArr[i]) return i+1;
      }
    }

    // TODO: Clean this guy up
    function AddWordToBoard(){
      var i, len, curIndex, curWord, curChar, curMatch, testWord, testChar,
          minMatchDiff = 9999, curMatchDiff;

      if(wordsActive.length < 1){
        curIndex = 0;
        for(i = 0, len = wordBank.length; i < len; i++){
          if (wordBank[i].totalMatches < wordBank[curIndex].totalMatches){
            curIndex = i;
          }
        }
        wordBank[curIndex].successfulMatches = [{x:12,y:12,dir:0}];
      }
      else{
        curIndex = -1;

        for(i = 0, len = wordBank.length; i < len; i++){
          curWord = wordBank[i];
          curWord.effectiveMatches = 0;
          curWord.successfulMatches = [];
          for(var j = 0, lenJ = curWord.char.length; j < lenJ; j++){
            if(j == 1) continue; // skip pos
            curChar = curWord.char[j];
            for (var k = 0, lenK = wordsActive.length; k < lenK; k++){
              testWord = wordsActive[k];
              for (var l = 0, lenL = testWord.char.length; l < lenL; l++){
                if(l == 1) continue; // skip pos
                testChar = testWord.char[l];
                if (curChar === testChar){
                  curWord.effectiveMatches++;

                  var curCross = {x:testWord.x,y:testWord.y,dir:0};
                  if(testWord.dir === 0){
                    curCross.dir = 1;
                    curCross.x += l;
                    curCross.y -= j;
                  }
                  else{
                    curCross.dir = 0;
                    curCross.y += l;
                    curCross.x -= j;
                  }

                  var isMatch = true;

                  for(var m = -1, lenM = curWord.char.length + 1; m < lenM; m++){
                    var crossVal = [];
                    if (m !== j){
                      if (curCross.dir === 0){
                        var xIndex = curCross.x + m;

                        if (xIndex < 0 || xIndex > board.length){
                          isMatch = false;
                          break;
                        }

                        crossVal.push(board[xIndex][curCross.y]);
                        crossVal.push(board[xIndex][curCross.y + 1]);
                        crossVal.push(board[xIndex][curCross.y - 1]);
                      }
                      else{
                        var yIndex = curCross.y + m;

                        if (yIndex < 0 || yIndex > board[curCross.x].length){
                          isMatch = false;
                          break;
                        }

                        crossVal.push(board[curCross.x][yIndex]);
                        crossVal.push(board[curCross.x + 1][yIndex]);
                        crossVal.push(board[curCross.x - 1][yIndex]);
                      }

                      if(m > -1 && m < lenM-1){
                        if (crossVal[0] !== curWord.char[m]){
                          if (crossVal[0] !== null){
                            isMatch = false;
                            break;
                          }
                          else if (crossVal[1] !== null){
                            isMatch = false;
                            break;
                          }
                          else if (crossVal[2] !== null){
                            isMatch = false;
                            break;
                          }
                        }
                      }
                      else if (crossVal[0] !== null){
                        isMatch = false;
                        break;
                      }
                    }
                  }

                  if (isMatch === true){
                    curWord.successfulMatches.push(curCross);
                  }
                }
              }
            }
          }

          curMatchDiff = curWord.totalMatches - curWord.effectiveMatches;

          if (curMatchDiff<minMatchDiff && curWord.successfulMatches.length>0){
            curMatchDiff = minMatchDiff;
            curIndex = i;
          }
          else if (curMatchDiff <= 0){
            return false;
          }
        }
      }

      if (curIndex === -1){
        return false;
      }

      var spliced = wordBank.splice(curIndex, 1);
      wordsActive.push(spliced[0]);

      var pushIndex = wordsActive.length - 1,
          rand = Math.random(),
          matchArr = wordsActive[pushIndex].successfulMatches,
          matchIndex = Math.floor(rand * matchArr.length),
          matchData = matchArr[matchIndex];

      wordsActive[pushIndex].x = matchData.x;
      wordsActive[pushIndex].y = matchData.y;
      wordsActive[pushIndex].dir = matchData.dir;

      let actualIndex = findWordIdx(wordsActive[pushIndex].string);
      console.log(wordsActive[pushIndex].string, " ~~~ ", actualIndex);
      for(i = 0, len = wordsActive[pushIndex].char.length; i < len; i++){
        var xIndex = matchData.x,
            yIndex = matchData.y;

        if (matchData.dir === 0){
          xIndex += i;
        }
        else{
          yIndex += i;
        }
        board[xIndex][yIndex] = wordsActive[pushIndex].char[i];
        isPos[xIndex][yIndex] = (i == 1);
        classesOfBoard[xIndex][yIndex].push(actualIndex);
        console.log(xIndex, yIndex, classesOfBoard[xIndex][yIndex]);

        Bounds.Update(xIndex,yIndex);
      }

      return true;
    }

  return (
    <>
    </>
  );
};

export default CrosswordGrid;
