import { useState } from "react";
import { getAuth, signInWithEmailAndPassword, User } from "firebase/auth";
import app from "../firebase-config";
// import { signify } from 'react-signify';

// const sUser = signify<User|null>(null)

const useAuth = () => {
    const [user, setUser] = useState<User | null>(null); // Allow state to be either a User object or null
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState<string | null>(null); // Error can be a string or null

    const login = async (email: string, password: string) => {
        const auth = getAuth(app);
        try {   
            const userCredential = await signInWithEmailAndPassword(auth, email, password);
            setUser(userCredential.user)
            // sUser.set(userCredential.user)
            console.log("userCredential", userCredential)
            return userCredential.user; // Return the authenticated user
        } catch (err) {
            const error = err as Error; // Assert that 'err' is of type 'Error'
            console.error("Error:", error.message);
        }
    };

    const logout = async () => {
        const auth = getAuth();
        try {
            await auth.signOut();
            setUser(null); // Reset the user state to null after logging out
            // sUser.set(null)
            console.log("User signed out");
        } catch (err: any) {
            console.error("Error signing out:", err.message);
        }
    };


    const getToken = async () => {
        if (sUser.value) {
            console.log("user", user)
            try {
                const token = await sUser.value.getIdToken(); // Retrieve the ID token
                return token;
            } catch (err: any) {
                console.error("Error fetching token:", err.message);
                setError(err.message);
            }
        }
        return null;
    };

    
    return { sUser, login, logout, getToken, loading, error };
};

export default useAuth;
