import Round1 from '../../layouts/RoundBase/Round1';
import User from '../../layouts/User/User';
import { listenToRoundStart } from '../../services/firebaseServices';
import { useEffect, useRef } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';

function UserRound1() {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();
    const roomId = searchParams.get("roomid") || "1";

    const isFirstCallback = useRef(true); // ⬅️ persists across renders

    useEffect(() => {
        const unsubscribePlayers = listenToRoundStart(roomId, (round) => {
            // Skip the very first callback
            if (isFirstCallback.current) {
                isFirstCallback.current = false;
                return;
            }

            navigate(`/play?round=${round || 1}&roomId=${roomId}`);
        });

        return () => {
            unsubscribePlayers();
        };
    }, [roomId, navigate]); // You can also include deps if needed

    return (
        <User
            QuestionComponent={<Round1 />}
        />
    );
}

export default UserRound1;
