import React, { useEffect } from 'react';
import { Score } from '../type';
import { usePlayer } from '../context/playerContext';
import { listenToScores } from '../services/firebaseServices';

function PlayerScore() {
    const { roomId, scoreList, setScoreList, setPlayerFlashes } = usePlayer();

    useEffect(() => {
        const unsubscribePlayers = listenToScores(roomId, (scoreList) => {
            console.log("scoreList", scoreList);
            if (Array.isArray(scoreList)) {
                const scoreListWithFlashes = scoreList.map((score: Score) => ({
                    ...score,
                    flashColor: score.score.isCorrect ? "flash-correct" : "flash-incorrect",
                }));

                setPlayerFlashes(scoreListWithFlashes);

                const sortedList = [...scoreList].sort((a, b) => parseInt(b.score) - parseInt(a.score));
                setScoreList(sortedList);
            }
        });

        return () => {
            unsubscribePlayers();
        };
    }, [roomId, setPlayerFlashes, setScoreList]); // Add dependencies for safety

    return (
        <div
            className="bg-white mt-4 p-4 rounded-lg shadow-md flex-1 relative"
            style={{ height: `${scoreList.length * 50}px` }}
        >
            {scoreList.map((player: Score, index: number) => (
                <div
                    key={player.playerName}
                    className="player-item absolute w-full flex items-center"
                    style={{
                        transform: `translateY(${index * 50}px)`,
                        transition: 'transform 0.5s ease',
                    }}
                >
                    <img
                        src={player.avatar}
                        alt={player.playerName}
                        className="w-8 h-8 rounded-full mr-2"
                    />
                    <div className="flex-1">
                        <p className="text-sm">{player.playerName}</p>
                        <div className="w-full bg-gray-200 text-center py-1 rounded-lg">
                            {`Score: ${player.score}`}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default PlayerScore;
