import React, { useEffect, useState } from 'react';
import { getTest, getTestByUserId } from './service';
import { Question } from '../../../type';


const ViewTest: React.FC = () => {
  const [testList, setTestList] = useState<string[]>([]); // Updated type to Test[]
  const [selectedTestName, setSelectedTestName] = useState<string>(""); // Track selected test
  const [testData, setTestData] = useState<{
    round_1: Question[];
    round_2: Question[];
    round_3: { [key: string]: Question[] };
    round_4: { [key: string]: Question[] };
  }>({
    round_1: [],
    round_2: [],
    round_3: {},
    round_4: {},
  });

  // Fetch the list of tests on component mount
  useEffect(() => {
    const getTestList = async () => {
      try {
        const testList = await getTestByUserId("mnbvcxz");
        setTestList(testList);
        // Optionally set the first test as default
      } catch (error) {
        console.error("Error fetching test list:", error);
      }
    };

    getTestList();
  }, []);

  // Fetch questions when a test is selected
  // useEffect(() => {
  //   const fetchTestQuestions = async () => {
  //     if (selectedTestId) {
  //       try {
  //         const testData = await getTest("mnbvcxz", selectedTestId); // Adjust params as needed
  //         // Assuming getTest returns questions directly or a structure like { questions: Exam[] }
  //         const questions = testData.questions || testData; // Adjust based on API response
  //         setExams(questions);
  //       } catch (error) {
  //         console.error("Error fetching test questions:", error);
  //         setExams([]);
  //       }
  //     }
  //   };

  //   fetchTestQuestions();
  // }, [selectedTestId]);

  // Handle dropdown change
  const handleTestChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedTestName(event.target.value);
  };

  const handleViewTest = async () => {
    if (!selectedTestName) {
      alert("Please select a test first!");
      return;
    }

    try {
      const data = await getTest(selectedTestName, "mnbvcxz");
      setTestData({
        round_1: data.round_1 || [],
        round_2: data.round_2 || [],
        round_3: data.round_3 || {},
        round_4: data.round_4 || {},
      });
    } catch (error) {
      console.error("Error fetching test questions:", error);
      setTestData({
        round_1: [],
        round_2: [],
        round_3: {},
        round_4: {},
      });
    }
  }

  // Hàm render bảng cho một danh sách câu hỏi
  const renderTable = (questions: Question[], title: string) => {
    if (questions.length === 0) return null;

    return (
      <div className="mb-6">
        <h3 className="text-xl font-semibold mb-2">{title}</h3>
        <table className="table-auto w-full border-collapse border border-gray-300">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2">#</th>
              <th className="border p-2">Question</th>
              <th className="border p-2">Answer</th>
              <th className="border p-2">Type</th>
              <th className="border p-2">Url</th>
            </tr>
          </thead>
          <tbody>
            {questions.map((question, index) => (
              <tr key={question.testId + index}>
                <td className="border p-2">{index + 1}</td>
                <td className="border p-2">{question.question}</td>
                <td className="border p-2">{question.answer}</td>
                <td className="border p-2">{question.type || 'N/A'}</td>
                <td className="border p-2">{question.imgUrl || 'N/A'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  // Hàm render bảng cho các vòng có nhóm (round_3, round_4)
  const renderGroupedTable = (groups: { [key: string]: Question[] }, roundTitle: string) => {
    if (Object.keys(groups).length === 0) return null;

    return (
      <div className="mb-6">
        <h3 className="text-xl font-semibold mb-2">{roundTitle}</h3>
        {Object.entries(groups).map(([groupName, questions]) => (
          <div key={groupName} className="mb-4">
            <h4 className="text-lg font-medium mb-2">{groupName}</h4>
            {renderTable(questions, "")}
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="card p-6 shadow-lg bg-white">
      <h2 className="text-2xl font-bold mb-4">Uploaded Exams</h2>
      <label className="block text-lg mb-2" htmlFor="testSelect">
        Chọn bộ đề
      </label>

      <select
        id="testSelect"
        name="testSelect"
        className="border rounded p-2 w-full mb-4"
        value={selectedTestName}
        onChange={handleTestChange}
      >
        <option value="" disabled>
          -- Select a Test --
        </option>
        {testList.map((test) => (
          <option key={test} value={test}>
            {test}
          </option>
        ))}
      </select>

      <button
        type="submit"
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        onClick={handleViewTest}
      >
        View 
      </button>

      {exams.length > 0 ? (
        <table className="table-auto w-full border-collapse border border-gray-300">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2">#</th>
              <th className="border p-2">Question</th>
              <th className="border p-2">Answer</th>
              <th className="border p-2">Type</th>
              <th className="border p-2">Url</th>
            </tr>
          </thead>
          <tbody>
            {exams.map((exam, index) => (
              <tr key={exam.testId + index}> {/* Unique key using testId + index */}
                <td className="border p-2">{index + 1}</td>
                <td className="border p-2">{exam.question}</td>
                <td className="border p-2">{exam.answer}</td>
                <td className="border p-2">{exam.type}</td>
                <td className="border p-2">{exam.imageUrl}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-gray-500">No questions available for this test.</p>
      )}
    </div>
  );
};

export default ViewTest;
