import React, { useState, useEffect } from 'react';
import Header from './Header';

function Play() {
    const [isChatOpen, setIsChatOpen] = useState(false);
    const [timeLeft, setTimeLeft] = useState(30); // Th·ªùi gian ƒë·∫øm ng∆∞·ª£c (30s)
    const [answer, setAnswer] = useState('');
    const [submitted, setSubmitted] = useState(false);

    // X·ª≠ l√Ω ƒë·∫øm ng∆∞·ª£c th·ªùi gian
    useEffect(() => {
        if (timeLeft > 0 && !submitted) {
            const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
            return () => clearTimeout(timer);
        }
    }, [timeLeft, submitted]);

    return (
        <div className="w-screen h-screen bg-gradient-to-r from-blue-500 to-teal-400 flex flex-col overflow-hidden">
            {/* Header */}
            <Header />

            {/* Main Game Section */}
            <div className="flex flex-1 p-4 gap-4">
                {/* Question Box (80%) */}
                <div className="w-4/5 flex flex-col">
                    {/* Thanh ƒë·∫øm th·ªùi gian */}
                    <div className="w-full h-2 bg-gray-300 rounded-full mb-2">
                        <div
                            className="h-full bg-red-500 rounded-full transition-all duration-1000"
                            style={{ width: `${(timeLeft / 30) * 100}%` }}
                        ></div>
                    </div>

                    {/* N·ªôi dung c√¢u h·ªèi */}
                    <div className="w-full h-[60%] bg-white rounded-lg shadow-md p-6 flex flex-col items-center justify-center">
                        <p className="text-gray-700 text-xl font-semibold text-center">What is the capital of France?</p>

                        {/* H√¨nh ·∫£nh/Video (T√πy v√≤ng) */}
                        <div className="mt-4 w-3/4">
                            <img
                                src="https://via.placeholder.com/300x200"
                                alt="Question Visual"
                                className="w-full h-auto rounded-lg"
                            />
                        </div>
                    </div>

                    {/* Answer Input */}
                    <div className="bg-white mt-6 p-4 rounded-lg shadow-md flex flex-col items-center">
                        <p className="text-sm font-bold">Your Answer</p>
                        <input
                            type="text"
                            className="w-full h-12 mt-2 border border-gray-300 rounded-lg px-3 text-lg"
                            placeholder="Type your answer..."
                            value={answer}
                            onChange={(e) => setAnswer(e.target.value)}
                            disabled={submitted}
                        />
                        <button
                            className="mt-2 bg-blue-500 text-white px-4 py-2 rounded-lg shadow-md"
                            onClick={() => setSubmitted(true)}
                            disabled={submitted}
                        >
                            Submit
                        </button>
                    </div>

                    {/* Hi·ªÉn th·ªã ƒë√°p √°n n·∫øu ƒë√£ tr·∫£ l·ªùi */}
                    {submitted && (
                        <div className="bg-green-500 text-white text-center p-4 rounded-lg mt-4">
                            <p className="text-lg font-bold">Correct Answer: Paris</p>
                        </div>
                    )}
                </div>

                {/* Sidebar (20%) */}
                <div className="w-1/5 flex flex-col">
                    {/* Round Indicator */}
                    <div className="bg-gray-300 text-center font-bold text-lg p-3 rounded-lg">
                        ROUND 1
                    </div>

                    {/* Player Scores */}
                    <div className="bg-white mt-4 p-4 rounded-lg shadow-md flex-1">
                        {Array(3).fill(0).map((_, index) => (
                            <div key={index} className="mb-4">
                                <p className="text-sm">Username</p>
                                <div className="w-full bg-gray-200 text-center py-2 rounded-lg">
                                    Score: {Math.floor(Math.random() * 20)}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Chat Toggle Button */}
            <button
                className="fixed bottom-6 right-6 bg-gray-800 text-white w-12 h-12 flex items-center justify-center rounded-full shadow-lg"
                onClick={() => setIsChatOpen(!isChatOpen)}
            >
                {isChatOpen ? "‚úñ" : "üí¨"}
            </button>

            {/* Chat Box */}
            {isChatOpen && (
                <div className="fixed bottom-20 right-6 w-80 h-60 bg-white shadow-lg rounded-lg p-4">
                    <p className="text-sm font-bold">Chat</p>
                    <div className="h-40 overflow-y-auto bg-gray-100 p-2 rounded-lg">
                        <p className="text-xs">User1: Hello!</p>
                        <p className="text-xs text-right">You: Hi there!</p>
                    </div>
                    <input
                        type="text"
                        className="w-full h-10 mt-2 border border-gray-300 rounded-lg px-2"
                        placeholder="Type a message..."
                    />
                </div>
            )}
        </div>
    );
}

export default Play;
