import React, { useEffect, useState } from 'react'
import { usePlayer } from '../context/playerContext';
import { User, Answer, Score } from '../type';
import { listenToBroadcastedAnswer } from '../services/firebaseServices';



const PlayerAnswer: React.FC = () => {
    const { playersArray, playerFlashes, roomId, triggerPlayerFlash, scoreList, setScoreList } = usePlayer()
    const [answerList, setAnswerList] = useState<Answer[]>([])
    const spots = [1, 2, 3, 4]


    useEffect(() => {
        const unsubscribeBroadcastedAnswer = listenToBroadcastedAnswer(roomId, (answerList) => {
            setAnswerList(answerList)
            console.log("answerList", answerList)

            // setTimeout(()=>{
            //     setAnswerList([])
            // },3000)
        })

        return () => {
            unsubscribeBroadcastedAnswer();
        };
    }, []);

    useEffect(() => {
        if (playerFlashes && playerFlashes.length > 0) {
            playerFlashes.forEach((index:number) => {
                playerFlashes[index].flashColor = player.isCorrect ? "flash-correct" : "flash-incorrect";
            });

            setTimeout(() => {
                playerFlashes.forEach((player: Score) => {
                    player.flashColor = ""
                });
            }, 3000)
        }
    }, [scoreList]);
    return (
        <>

            <div className="flex justify-around mt-4">
                {
                    spots.map((spotNumber: number) => {

                        const storedPlayers = localStorage.getItem("playerList");
                        { console.log("storedPlayers", storedPlayers) }
                        const array = playersArray !== null
                            ? playersArray
                            : (storedPlayers ? JSON.parse(storedPlayers) : []);
                        { console.log("array", array) }
                        const player = array.find((p: User) => parseInt(p.stt) === spotNumber);

                        const answer = Array.isArray(answerList) && answerList.length != 0 ? answerList.find((answer: Answer) => parseInt(answer.stt) === spotNumber) : null
                        if (player) {
                            return (
                                <div key={spotNumber} className={`flex flex-col items-center 
                                ${playerFlashes[spotNumber] ? playerFlashes[spotNumber].flashColor : ""}
                                
                                `}>
                                    <img
                                        src={player.avatar}
                                        alt="Player"
                                        className="w-16 h-16 rounded-full"
                                    />
                                    <p className="text-white mt-2 ">
                                        {answer && answer.answer}
                                    </p>
                                    <p className="text-white">{`player_${player.stt}: ${player.userName}`}</p>

                                </div>
                            )
                        }

                    })
                }
                {/* {playersArray.map((player, index) => (
                           
                        ))} */}
            </div>

        </>
    )
}

export default PlayerAnswer
