import axios from "axios";
import { useEffect, useState, createContext, useContext } from "react";
import useAuth from "../hooks/useAuth"; // Assuming this is your custom hook file
import {
  getAuth,
  signInWithEmailAndPassword,
  User,
  signOut,
  onAuthStateChanged,
} from "firebase/auth";

// Create a context for Axios with Authentication
const AxiosAuthContext = createContext<any>(null);

export const AxiosAuthProvider = ({ children }: { children: React.ReactNode }) => {
  const [axiosInstance, setAxiosInstance] = useState(() => axios.create());
  const [user, setUser] = useState<User | null>(null);
  const [authToken, setAuthToken] = useState<string | undefined>(undefined);

  const saveToken = (token: string | undefined) => {
    setAuthToken(token);
  };

  const clearToken = () => {
    setAuthToken(undefined);
  };

  useEffect(() => {
    const initializeAxios = async () => {
      const userToken = await user?.getIdToken();
      saveToken(userToken) // Fetch Bearer token from Firebase Auth
      const newAxiosInstance = axios.create({
        baseURL: "http://localhost:8000", // Replace with your API endpoint
      });

      // Add interceptor to include token in the Authorization header
      newAxiosInstance.interceptors.request.use(
        (config) => {
          if (authToken) {
            config.headers.Authorization = `Bearer ${authToken}`;
          }
          return config;
        },
        (error) => {
          console.error("Request error:", error);
          return Promise.reject(error);
        }
      );

      setAxiosInstance(newAxiosInstance);
    };

    initializeAxios();
  }, [authToken]);

  return (
    <AxiosAuthContext.Provider value={{axiosInstance, saveToken, clearToken}}>
      {children}
    </AxiosAuthContext.Provider>
  );
};

// Custom hook to use Axios with Bearer authentication
export const useAxiosAuth = () => {
  return useContext(AxiosAuthContext);
};
