import Play from "../../layouts/Play";
import React, { useState, useEffect } from "react";


interface HintWord {
    word: string;
    x: number;
    y: number;
    direction: "horizontal" | "vertical";
}

interface ObstacleQuestionBoxProps {
    obstacleWord: string;
}

// T·ª´ kh√≥a ch∆∞·ªõng ng·∫°i v·∫≠t
const mainKeyword = "BOCONGANH";

// T·∫°o t·ª´ kh√≥a g·ª£i √Ω v·ªõi v·ªã tr√≠ v√† h∆∞·ªõng random
const generateHintWords = (words: string[], gridSize: number, obstacleWord: string): HintWord[] => {
    const placedWords: HintWord[] = []; // Danh s√°ch t·ª´ ƒë√£ ƒë·∫∑t
    const grid: string[][] = Array(gridSize).fill(null).map(() => Array(gridSize).fill("")); // M·∫£ng ki·ªÉm tra v·ªã tr√≠

    const findCommonLetterPairs = (words: string[]): { word1: string; word2: string; commonLetters: string[] }[] => {
        let results: { word1: string; word2: string; commonLetters: string[] }[] = [];
        let usedWords = new Set<string>(); // Theo d√µi t·ª´ ƒë√£ ch·ªçn
    
        for (let i = 0; i < words.length; i++) {
            for (let j = i + 1; j < words.length; j++) {
                const word1 = words[i];
                const word2 = words[j];
    
                // N·∫øu c·∫£ 2 t·ª´ ƒë√£ ƒë∆∞·ª£c d√πng trong c·∫∑p tr∆∞·ªõc, b·ªè qua
                if (usedWords.has(word1) && usedWords.has(word2)) continue;
    
                // T√¨m k√Ω t·ª± chung
                const commonLetters = [...new Set(word1)].filter(letter => word2.includes(letter));
    
                if (commonLetters.length >= 2) {
                    // T√≠nh tr·ªçng s·ªë: ∆Øu ti√™n k√Ω t·ª± g·∫ßn trung t√¢m
                    const scoredLetters = commonLetters.map(letter => {
                        const index1 = word1.indexOf(letter);
                        const index2 = word2.indexOf(letter);
                        const center1 = word1.length / 2;
                        const center2 = word2.length / 2;
                        const score = Math.abs(index1 - center1) + Math.abs(index2 - center2);
                        return { letter, score };
                    });
    
                    // S·∫Øp x·∫øp theo ƒëi·ªÉm ∆∞u ti√™n th·∫•p nh·∫•t (g·∫ßn trung t√¢m h∆°n)
                    scoredLetters.sort((a, b) => a.score - b.score);
    
                    // L·∫•y ra c√°c ch·ªØ c√°i ∆∞u ti√™n nh·∫•t
                    const prioritizedLetters = scoredLetters.map(item => item.letter);
    
                    results.push({ word1, word2, commonLetters: prioritizedLetters });
    
                    // ƒê√°nh d·∫•u t·ª´ ƒë√£ d√πng
                    usedWords.add(word1);
                    usedWords.add(word2);
    
                    // N·∫øu ƒë√£ ƒë·ªß 2 c·∫∑p, d·ª´ng l·∫°i
                    if (results.length >= 2) return results;
                }
            }
        }
    
        return results;
    };
    
    // üõ† Test
    const testWords = ["h·∫°tgi·ªëng", "c√∫c", "thu·ªëc", "dream", "gi√≥", "ch√¢uki·ªátlu√¢n"];
    console.log(findCommonLetterPairs(testWords));
    const canPlaceWord = (word: string, x: number, y: number, direction: "horizontal" | "vertical") => {
        for (let i = 0; i < word.length; i++) {
            const newX = direction === "horizontal" ? x + i : x;
            const newY = direction === "horizontal" ? y : y + i;
    
            if (newX >= gridSize || newY >= gridSize) return false; // V∆∞·ª£t gi·ªõi h·∫°n
    
            // N·∫øu √¥ ƒë√£ c√≥ k√Ω t·ª± kh√°c t·ª´ tr∆∞·ªõc, ki·ªÉm tra xem c√≥ tr√πng v·ªõi k√Ω t·ª± c·∫ßn ƒë·∫∑t kh√¥ng
            if (grid[newY][newX] && grid[newY][newX] !== word[i]) return false;
        }
        return true;
    };

    words.forEach((word) => {
        let placed = false;
        for (let attempt = 0; attempt < 100; attempt++) { // Th·ª≠ ƒë·∫∑t t·ªëi ƒëa 100 l·∫ßn
            const direction: "horizontal" | "vertical" = Math.random() > 0.5 ? "horizontal" : "vertical";
            let x = direction === "horizontal" ? Math.floor(Math.random() * (gridSize - word.length + 1)) : Math.floor(Math.random() * gridSize);
            let y = direction === "vertical" ? Math.floor(Math.random() * (gridSize - word.length + 1)) : Math.floor(Math.random() * gridSize);

            if (canPlaceWord(word, x, y, direction)) {
                // Ghi v√†o grid ƒë·ªÉ ki·ªÉm tra sau
                for (let i = 0; i < word.length; i++) {
                    const newX = direction === "horizontal" ? x + i : x;
                    const newY = direction === "horizontal" ? y : y + i;
                    grid[newY][newX] = word[i];
                }

                placedWords.push({ word, x, y, direction });
                placed = true;
                break;
            }
        }
        if (!placed) console.warn(`Kh√¥ng th·ªÉ ƒë·∫∑t t·ª´: ${word}`);
    });

    return placedWords;
};

// K√≠ch th∆∞·ªõc l∆∞·ªõi
const GRID_SIZE = 10;

// T·∫°o l∆∞·ªõi tr·ªëng
const generateEmptyGrid = () => {
    return Array(GRID_SIZE).fill(null).map(() => Array(GRID_SIZE).fill(""));
};


const ObstacleQuestionBox: React.FC<ObstacleQuestionBoxProps> = ({ obstacleWord }) => {
    const [grid, setGrid] = useState<string[][]>(generateEmptyGrid());
    const [revealedRows, setRevealedRows] = useState<boolean[]>(Array(GRID_SIZE).fill(false));
    const [revealedCols, setRevealedCols] = useState<boolean[]>(Array(GRID_SIZE).fill(false));
    const [hintWords, setHintWords] = useState<HintWord[]>([]);


    useEffect(() => {

        const randomHintWords = generateHintWords(["GI√ì", "H·∫†T", "DREAM", "THU·ªêC", "C√öC", "CH√ÇU"], GRID_SIZE, mainKeyword);
        setHintWords(randomHintWords);
        let newGrid = generateEmptyGrid();

        // Th√™m t·ª´ v√†o l∆∞·ªõi
        randomHintWords.forEach(({ word, x, y, direction }) => {
            for (let i = 0; i < word.length; i++) {
                if (direction === "horizontal") {
                    newGrid[y][x + i] = word[i];
                } else {
                    newGrid[y + i][x] = word[i];
                }
            }
        });

        setGrid(newGrid);
    }, []);

    // H√†m toggle hi·ªÉn th·ªã h√†ng/c·ªôt
    const toggleRow = (rowIndex: number) => {
        setRevealedRows((prev) => {
            const newRevealed = [...prev];
            newRevealed[rowIndex] = !newRevealed[rowIndex];
            return newRevealed;
        });
    };

    const toggleCol = (colIndex: number) => {
        setRevealedCols((prev) => {
            const newRevealed = [...prev];
            newRevealed[colIndex] = !newRevealed[colIndex];
            return newRevealed;
        });
    };

    return (
        <div className="flex flex-col items-center bg-white rounded-lg shadow-md p-6">
            <div className="grid grid-cols-[30px_repeat(10,40px)] gap-1">
                {/* H√†ng ƒë·∫ßu ti√™n ch·ª©a s·ªë c·ªôt */}
                <div></div> {/* √î tr·ªëng ·ªü g√≥c */}
                {Array.from({ length: GRID_SIZE }).map((_, colIndex) => (
                    <button
                        key={colIndex}
                        className="font-bold text-blue-500 cursor-pointer"
                        onClick={() => toggleCol(colIndex)}
                    >
                        {colIndex + 1}
                    </button>
                ))}

                {/* Hi·ªÉn th·ªã l∆∞·ªõi ch·ªØ */}
                {grid.map((row, rowIndex) => (
                    <React.Fragment key={rowIndex}>
                        {/* S·ªë h√†ng b√™n tr√°i */}
                        <button
                            className="font-bold text-blue-500 cursor-pointer"
                            onClick={() => toggleRow(rowIndex)}
                        >
                            {rowIndex + 1}
                        </button>

                        {row.map((cell, colIndex) => {
                            const isRevealed =
                                (revealedRows[rowIndex] && hintWords.some((word) => word.y === rowIndex && word.direction === "horizontal")) ||
                                (revealedCols[colIndex] && hintWords.some((word) => word.x === colIndex && word.direction === "vertical"));

                            return (
                                <div
                                    key={colIndex}
                                    className={`w-10 h-10 border flex items-center justify-center text-lg font-semibold select-none 
                                    ${isRevealed ? "text-black" : "text-transparent"}
                                    ${obstacleWord.includes(cell) && isRevealed ? "font-bold text-red-500" : ""}
                                    ${cell === "" ? "border-none" : "border-gray-400"}
                                `}
                                >
                                    {cell}
                                </div>
                            );
                        })}
                    </React.Fragment>
                ))}
            </div>
        </div>
    );
};

function Round2() {
    return <Play questionComponent={<ObstacleQuestionBox obstacleWord={mainKeyword} />} />;
}

export default Round2;
